import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import AdminHeader from "../page/AdminHeader";
import { Upload, XCircle } from "lucide-react";

export default function AddProduct() {
  const navigate = useNavigate();
  const token = localStorage.getItem("adminToken");

  const [form, setForm] = useState({
    name: "",
    brand: "",
    price: "",
    quantityInStock: "",
    screenSize: "",
    ram: "",
    storage: "",
    chipset: "",
    camera: "",
    battery: "",
    os: "",
    color: "",
    description: "",
  });

  const [images, setImages] = useState([]);
  const [preview, setPreview] = useState([]);

  if (!token) {
    navigate("/admin/login");
    return null;
  }

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    setImages([...images, ...files]);
    const urls = files.map((f) => URL.createObjectURL(f));
    setPreview([...preview, ...urls]);
  };

  const removeImage = (i) => {
    const copyImg = [...images];
    const copyPrev = [...preview];
    copyImg.splice(i, 1);
    copyPrev.splice(i, 1);
    setImages(copyImg);
    setPreview(copyPrev);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    Object.keys(form).forEach((k) => data.append(k, form[k]));
    images.forEach((img) => data.append("images", img));

    try {
      await axios.post("http://localhost:8080/api/products", data, {
        headers: {
          Authorization: "Bearer " + token,
          "Content-Type": "multipart/form-data",
        },
      });

      alert("Thêm thành công!");
      navigate("/admin/products");
    } catch (err) {
      if (err.response?.status === 401) {
        localStorage.removeItem("adminToken");
        navigate("/admin/login");
      }
      alert("Lỗi khi thêm!");
    }
  };

  return (
    <>
      <AdminHeader />
      <div style={{ height: 70 }}></div>

      <div style={styles.wrapper}>
        <h2 style={styles.pageTitle}>Thêm sản phẩm mới</h2>

        <form onSubmit={handleSubmit} style={styles.formContainer}>
          
          {/* LEFT */}
          <div style={styles.leftCol}>

            <div style={styles.card}>
              <h3 style={styles.cardTitle}>Thông tin cơ bản</h3>

              <label style={styles.label}>Tên sản phẩm</label>
              <input name="name" style={styles.input} onChange={handleChange} required />

              <label style={styles.label}>Thương hiệu</label>
              <input name="brand" style={styles.input} onChange={handleChange} required />

              <label style={styles.label}>Màu sắc</label>
              <input name="color" style={styles.input} onChange={handleChange} required />

              <label style={styles.label}>Mô tả</label>
              <textarea name="description" style={styles.textarea} onChange={handleChange} />
            </div>


            <div style={styles.card}>
              <h3 style={styles.cardTitle}>Thông số kỹ thuật</h3>

              {[
                "screenSize",
                "ram",
                "storage",
                "chipset",
                "camera",
                "battery",
                "os",
              ].map((item) => (
                <input
                  key={item}
                  name={item}
                  placeholder={item}
                  onChange={handleChange}
                  style={styles.input}
                />
              ))}
            </div>
          </div>

          {/* RIGHT */}
          <div style={styles.rightCol}>
            
            <div style={styles.card}>
              <h3 style={styles.cardTitle}>Giá - Kho</h3>

              <label style={styles.label}>Giá</label>
              <input name="price" type="number" style={styles.input} onChange={handleChange} required />

              <label style={styles.label}>Số lượng tồn</label>
              <input name="quantityInStock" type="number" style={styles.input} onChange={handleChange} required />
            </div>

            <div style={styles.card}>
              <h3 style={styles.cardTitle}>Ảnh sản phẩm</h3>

              <label style={styles.uploadLabel}>
                <Upload />
                Chọn ảnh
                <input multiple type="file" style={{ display: "none" }} onChange={handleImageUpload} />
              </label>

              <div style={styles.previewContainer}>
                {preview.map((img, i) => (
                  <div key={i} style={styles.previewBox}>
                    <img src={img} style={styles.previewImg} alt="" />
                    <button style={styles.removeImg} type="button" onClick={() => removeImage(i)}>
                      <XCircle color="red" size={18} />
                    </button>
                  </div>
                ))}
              </div>
            </div>

            <button style={styles.submitBtn}>Thêm sản phẩm</button>
          </div>
        </form>
      </div>
    </>
  );
}

const styles = {
  wrapper: { maxWidth: 1350, margin: "auto", padding: 20 },

  pageTitle: { fontSize: 26, fontWeight: 600, marginBottom: 20 },

  formContainer: {
    display: "flex",
    gap: 25,
    alignItems: "flex-start",
  },

  leftCol: { flex: 2, display: "flex", flexDirection: "column", gap: 20 },
  rightCol: { flex: 1.2, display: "flex", flexDirection: "column", gap: 20 },

  card: {
    background: "#fff",
    padding: 20,
    borderRadius: 10,
    border: "1px solid #eee",
  },

  cardTitle: { fontSize: 18, fontWeight: 600, marginBottom: 15 },

  label: { fontWeight: 600, fontSize: 14, marginBottom: 6 },

  input: {
    width: "100%",
    padding: "10px 12px",
    borderRadius: 6,
    border: "1px solid #ccc",
    marginBottom: 15,
  },

  textarea: {
    width: "100%",
    minHeight: 120,
    padding: "10px 12px",
    borderRadius: 6,
    border: "1px solid #ccc",
    marginBottom: 15,
  },

  uploadLabel: {
    padding: "10px 12px",
    border: "1px dashed #aaa",
    borderRadius: 6,
    display: "inline-flex",
    gap: 8,
    cursor: "pointer",
    color: "#555",
    marginBottom: 15,
  },

  previewContainer: {
    display: "flex",
    gap: 12,
    flexWrap: "wrap",
  },

  previewBox: {
    position: "relative",
    width: 100,
    height: 100,
    borderRadius: 6,
    overflow: "hidden",
    border: "1px solid #ddd",
  },

  previewImg: {
    width: "100%",
    height: "100%",
    objectFit: "cover",
  },

  removeImg: {
    position: "absolute",
    top: 4,
    right: 4,
    background: "transparent",
    border: "none",
    cursor: "pointer",
  },

  submitBtn: {
    background: "#ee4d2d",
    marginTop: 10,
    color: "#fff",
    padding: "12px 18px",
    borderRadius: 6,
    border: "none",
    cursor: "pointer",
    fontSize: 15,
    display: "flex",
    gap: 6,
    alignItems: "center",
    justifyContent: "center",
  },
};
