import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import AdminHeader from "..";
import { Upload, XCircle, Plus } from "lucide-react";

export default function AddProduct() {
  const navigate = useNavigate();
  const adminToken = localStorage.getItem("adminToken");

  const [form, setForm] = useState({
    name: "",
    brand: "",
    price: "",
    quantityInStock: "",
    screenSize: "",
    ram: "",
    storage: "",
    chipset: "",
    camera: "",
    battery: "",
    os: "",
    color: "",
    description: "",
  });

  const [images, setImages] = useState([]);
  const [preview, setPreview] = useState([]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);

    setImages([...images, ...files]);

    const previewUrls = files.map((file) => URL.createObjectURL(file));
    setPreview([...preview, ...previewUrls]);
  };

  const removeImage = (index) => {
    const newImgs = [...images];
    const newPrev = [...preview];

    newImgs.splice(index, 1);
    newPrev.splice(index, 1);

    setImages(newImgs);
    setPreview(newPrev);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    Object.keys(form).forEach((key) => {
      data.append(key, form[key]);
    });

    images.forEach((img) => {
      data.append("images", img);
    });

    try {
      await axios.post(
        "http://localhost:8080/api/admin/products",
        data,
        {
          headers: {
            Authorization: `Bearer ${adminToken}`,
            "Content-Type": "multipart/form-data",
          },
        }
      );

      alert("Thêm sản phẩm thành công!");
      navigate("/admin/products");

    } catch (error) {
      console.error(error);
      alert("Lỗi khi thêm sản phẩm!");
    }
  };

  return (
    <>
      <AdminHeader />
      <div style={{ height: 70 }}></div>

      <div style={styles.wrapper}>
        <h2 style={styles.pageTitle}>Thêm sản phẩm mới</h2>

        <form onSubmit={handleSubmit} style={styles.formContainer}>

          <div style={styles.leftCol}>
            <div style={styles.card}>
              <h3 style={styles.cardTitle}>Thông tin cơ bản</h3>

              <label style={styles.label}>Tên sản phẩm</label>
              <input name="name" value={form.name} onChange={handleChange} style={styles.input} required />

              <label style={styles.label}>Thương hiệu</label>
              <input name="brand" value={form.brand} onChange={handleChange} style={styles.input} required />

              <label style={styles.label}>Màu sắc</label>
              <input name="color" value={form.color} onChange={handleChange} style={styles.input} required />

              <label style={styles.label}>Mô tả</label>
              <textarea
                name="description"
                value={form.description}
                onChange={handleChange}
                style={styles.textarea}
                required
              />
            </div>

            <div style={styles.card}>
              <h3 style={styles.cardTitle}>Thông số kỹ thuật</h3>

              <label style={styles.label}>Màn hình</label>
              <input name="screenSize" value={form.screenSize} onChange={handleChange} style={styles.input} />

              <label style={styles.label}>RAM</label>
              <input name="ram" value={form.ram} onChange={handleChange} style={styles.input} />

              <label style={styles.label}>Bộ nhớ</label>
              <input name="storage" value={form.storage} onChange={handleChange} style={styles.input} />

              <label style={styles.label}>Chipset</label>
              <input name="chipset" value={form.chipset} onChange={handleChange} style={styles.input} />

              <label style={styles.label}>Camera</label>
              <input name="camera" value={form.camera} onChange={handleChange} style={styles.input} />

              <label style={styles.label}>Pin</label>
              <input name="battery" value={form.battery} onChange={handleChange} style={styles.input} />

              <label style={styles.label}>Hệ điều hành</label>
              <input name="os" value={form.os} onChange={handleChange} style={styles.input} />
            </div>
          </div>

          {/* RIGHT */}
          <div style={styles.rightCol}>
            <div style={styles.card}>
              <h3 style={styles.cardTitle}>Giá & Kho</h3>

              <label style={styles.label}>Giá</label>
              <input
                name="price"
                type="number"
                value={form.price}
                onChange={handleChange}
                style={styles.input}
                required
              />

              <label style={styles.label}>Số lượng tồn kho</label>
              <input
                name="quantityInStock"
                type="number"
                value={form.quantityInStock}
                onChange={handleChange}
                style={styles.input}
                required
              />
            </div>

            <div style={styles.card}>
              <h3 style={styles.cardTitle}>Hình ảnh sản phẩm</h3>

              <label style={styles.uploadLabel}>
                <Upload size={20} /> Chọn ảnh
                <input type="file" multiple accept="image/*" onChange={handleImageUpload} style={{ display: "none" }} />
              </label>

              <div style={styles.previewContainer}>
                {preview.map((img, index) => (
                  <div key={index} style={styles.previewBox}>
                    <img src={img} style={styles.previewImg} alt="preview" />
                    <button
                      type="button"
                      style={styles.removeImg}
                      onClick={() => removeImage(index)}
                    >
                      <XCircle size={20} color="red" />
                    </button>
                  </div>
                ))}
              </div>
            </div>

            <button type="submit" style={styles.submitBtn}>
              <Plus size={18} /> Thêm sản phẩm
            </button>
          </div>
        </form>
      </div>
    </>
  );
}

/* =============== STYLE giữ nguyên ============== */

const styles = {
  wrapper: { maxWidth: 1350, margin: "auto", padding: 20 },
  pageTitle: { fontSize: 26, fontWeight: 600, marginBottom: 20 },
  formContainer: { display: "flex", gap: 25, alignItems: "flex-start" },
  leftCol: { flex: 2, display: "flex", flexDirection: "column", gap: 20 },
