import React, { useContext, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { Link, useNavigate } from "react-router-dom";
import { DataContext } from "../DataContext";
import PageHeader from "../components/page/PageHeader";

function LoginPage() {
  const navigate = useNavigate();
  const [data, setData] = useState({ email: "", password: "" });
  const [loading, setLoading] = useState(false);

  // üî• L·∫§Y C·∫¢ setUser
  const { setToken, setUser } = useContext(DataContext);

  const handleChangeInput = (e) => {
    const { name, value } = e.target;
    setData({ ...data, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const res = await axios.post(
        "http://localhost:8080/api/auth/login",
        data
      );

      const accessToken =
        res.data.accessToken ||
        res.data.access_token ||
        res.data.token;

      if (!accessToken) {
        toast.error("Token kh√¥ng h·ª£p l·ªá t·ª´ server");
        return;
      }

      // üî• SET TOKEN
      setToken(accessToken);

      // üî• L·∫§Y USER
      const meRes = await axios.get(
        "http://localhost:8080/api/auth/user/me",
        {
          headers: {
            Authorization: `Bearer ${accessToken}`,
          },
        }
      );

      // üî• SET USER (QUAN TR·ªåNG NH·∫§T)
      setUser(meRes.data.data || meRes.data);

      toast.success("ƒêƒÉng nh·∫≠p th√†nh c√¥ng");
      navigate("/");

    } catch (err) {
      toast.error("Sai email ho·∫∑c m·∫≠t kh·∫©u");
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <PageHeader />

      <div
        className="bg-light min-vh-100 d-flex justify-content-center align-items-center"
        style={{ background: "#fffaf5" }}
      >
        <div className="col-md-5 col-lg-4">
          <div className="card border-0 shadow-lg rounded-4">
            <div className="card-body p-5">
              <h2 className="text-center fw-bold mb-2" style={{ color: "#ff7a29" }}>
                Welcome Back
              </h2>

              <p className="text-center text-muted mb-5">
                Please login to continue
              </p>

              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label className="form-label fw-semibold">Email</label>
                  <input
                    type="email"
                    name="email"
                    className="form-control rounded-pill px-3"
                    onChange={handleChangeInput}
                    required
                  />
                </div>

                <div className="mb-4">
                  <label className="form-label fw-semibold">Password</label>
                  <input
                    type="password"
                    name="password"
                    className="form-control rounded-pill px-3"
                    onChange={handleChangeInput}
                    required
                  />
                </div>

                <div className="d-grid">
                  <button
                    type="submit"
                    className="btn btn-lg text-white rounded-pill shadow"
                    style={{
                      background: "linear-gradient(135deg, #ff7a29, #ff9e57)",
                    }}
                    disabled={loading}
                  >
                    {loading ? "Logging in..." : "Login"}
                  </button>
                </div>
              </form>

              <p className="text-center text-muted mt-4 mb-0">
                Don‚Äôt have an account?{" "}
                <Link to="/register" style={{ color: "#ff7a29" }}>
                  Sign up
                </Link>
              </p>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default LoginPage;
