import React, { useEffect, useState } from "react";
import axios from "axios";
import "../../css/admin/page/AdminOrdersPage.css";
import AdminHeader from "./AdminHeader";

export default function AdminOrdersPage() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    loadOrders();
  }, []);

  const loadOrders = async () => {
    const token = localStorage.getItem("adminToken");

    try {
      const res = await axios.get("http://localhost:8080/api/orders", {
        headers: { Authorization: `Bearer ${token}` },
      });

      setOrders(res.data || []);

    } catch (err) {
      console.log("ERR =====> ", err.response?.data);
    }
  };

  const changeStatus = async (id, status) => {
    const token = localStorage.getItem("adminToken");

    try {
      await axios.put(
        `http://localhost:8080/api/orders/${id}/status`,
        { status },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      loadOrders();
    } catch (err) {
      alert(err.response?.data || "L·ªói khi c·∫≠p nh·∫≠t tr·∫°ng th√°i");
    }
  };

  const renderStatus = (s) => {
    const icons = {
      PENDING: "‚è≥",
      CONFIRMED: "‚úîÔ∏è",
      SHIPPING: "üöö",
      COMPLETED: "üéâ",
      CANCELLED: "‚ùå",
    };
    const labels = {
      PENDING: "Ch·ªù x√°c nh·∫≠n",
      CONFIRMED: "ƒê√£ x√°c nh·∫≠n",
      SHIPPING: "ƒêang giao",
      COMPLETED: "Giao th√†nh c√¥ng",
      CANCELLED: "ƒê√£ h·ªßy",
    };
    return `${icons[s]} ${labels[s]}`;
  };

  return (
    <>
      <AdminHeader />

      <div className="admin-orders">
        <h2>Qu·∫£n l√Ω ƒë∆°n h√†ng</h2>

        {orders.length === 0 && <p>Kh√¥ng c√≥ ƒë∆°n n√†o</p>}

        <table>
          <thead>
            <tr>
              <th>#</th>
              <th>Kh√°ch h√†ng</th>
              <th>Th√¥ng tin li√™n h·ªá</th>
              <th>ƒê·ªãa ch·ªâ</th>
              <th>T·ªïng ti·ªÅn</th>
              <th>Tr·∫°ng th√°i</th>
              <th>C·∫≠p nh·∫≠t</th>
            </tr>
          </thead>

          <tbody>
            {orders.map((o) => (
              <tr key={o.id}>
                <td>#{o.id}</td>

                <td>
                  <strong>{o.fullName}</strong>
                </td>

                <td>
                  <div>{o.phone}</div>
                </td>

                <td style={{ maxWidth: 200 }}>{o.address}</td>

                <td style={{ fontWeight: "600", color: "#f55" }}>
                  {o.totalPrice.toLocaleString()} ‚Ç´
                </td>

                <td>
                  <span className={`st-${o.status}`}>{renderStatus(o.status)}</span>
                </td>

                <td>
                  <select
                    disabled={o.status === "COMPLETED"}
                    value={o.status}
                    onChange={(e) => changeStatus(o.id, e.target.value)}
                  >
                    <option value="PENDING">Ch·ªù x√°c nh·∫≠n</option>
                    <option value="CONFIRMED">ƒê√£ x√°c nh·∫≠n</option>
                    <option value="SHIPPING">ƒêang giao</option>
                    <option value="COMPLETED">Ho√†n th√†nh</option>
                    <option value="CANCELLED">ƒê√£ h·ªßy</option>
                  </select>

                  {o.status === "COMPLETED" && (
                    <div style={{ fontSize: 12, color: "gray" }}>
                      * ƒê∆°n ƒë√£ ho√†n th√†nh
                    </div>
