import React, { useState, useEffect } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useLocation, useNavigate } from "react-router-dom";

function VerifyOtpPage() {
  const location = useLocation();
  const navigate = useNavigate();

  const emailFromRegister = location.state?.email || "";
  const [data, setData] = useState({
    email: emailFromRegister,
    otpCode: "",
  });
  const [loading, setLoading] = useState(false);

  const handleChangeInput = (e) => {
    const { name, value } = e.target;
    setData({ ...data, [name]: value });
  };

  async function handleVerify(e) {
    e.preventDefault();
    try {
      setLoading(true);
      const res = await axios.post(
        "http://localhost:8080/api/auth/verify-otp",
        data
      );

      if (res.status === 200) {
        toast.success("X√°c minh t√†i kho·∫£n th√†nh c√¥ng üéâ", {
          position: "top-right",
        });
        setData({ email: "", otpCode: "" });
        setTimeout(() => {
          navigate("/login");
        }, 1500);
      }
    } catch (error) {
      console.error("Verify OTP error:", error);
      toast.error("OTP kh√¥ng h·ª£p l·ªá ", { position: "top-right" });
    } finally {
      setLoading(false);
    }
  }

  return (
    <div
      className="bg-light min-vh-100 d-flex justify-content-center align-items-center"
      style={{ background: "#fffaf5" }}
    >
      <div className="col-md-5">
        <div
          className="card border-0 shadow-lg rounded-4"
          style={{ background: "#ffffff" }}
        >
          <div className="card-body p-5">
            <h2
              className="text-center mb-4 fw-bold"
              style={{ color: "#ff7a29" }}
            >
              Verify Your Account
            </h2>
            <p className="text-center text-muted mb-5">
              Enter your email and the OTP code sent to you
            </p>

            <form onSubmit={handleVerify} method="post">
              {/* Email */}
              <div className="mb-3">
                <label htmlFor="email" className="form-label fw-semibold">
                  Email address
                </label>
                <input
                  type="email"
                  className="form-control rounded-pill px-3"
                  id="email"
                  name="email"
                  value={data.email}
                  onChange={handleChangeInput}
                  placeholder="Enter your email"
                  disabled={loading}
                />
              </div>

              {/* OTP */}
              <div className="mb-3">
                <label htmlFor="otpCode" className="form-label fw-semibold">
                  OTP Code
                </label>
                <input
                  type="text"
                  className="form-control rounded-pill px-3"
                  id="otpCode"
                  name="otpCode"
                  value={data.otpCode}
                  onChange={handleChangeInput}
                  placeholder="Enter the OTP"
                  disabled={loading}
                />
              </div>

              {/* Verify Button */}
              <div className="d-grid">
                <button
                  type="submit"
                  className="btn btn-lg text-white rounded-pill shadow d-flex align-items-center justify-content-center"
                  style={{
                    background: "linear-gradient(135deg, #ff7a29, #ff9e57)",
                    border: "none",
                    opacity: loading ? 0.8 : 1,
                  }}
                  disabled={loading}
                >
                  {loading ? (
                    <>
                      <span
                        className="spinner-border spinner-border-sm me-2"
                        role="status"
                        aria-hidden="true"
                      ></span>
                      Verifying...
                    </>
                  ) : (
                    "Verify Account"
                  )}
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>

      {/* Toast */}
      <ToastContainer />
    </div>
  );
}

export default VerifyOtpPage;
