import React, { useContext, useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { DataContext } from "../../DataContext";
import "./PageHeader.css";

export default function PageHeader({ user }) {
  const navigate = useNavigate();
  const { logout } = useContext(DataContext);

  const [openMenu, setOpenMenu] = useState(false);
  const [cartCount, setCartCount] = useState(0);

  /* ===== CART COUNT ===== */
  useEffect(() => {
    if (!user?.email) {
      setCartCount(0);
      return;
    }

    const key = `cart_${user.email}`;
    const cart = JSON.parse(localStorage.getItem(key)) || [];
    const total = cart.reduce((sum, i) => sum + (i.quantity || 1), 0);
    setCartCount(total);
  }, [user]);

  return (
    <header className="header">
      <div className="header-inner">
        {/* LOGO */}
        <div className="logo" onClick={() => navigate("/")}>
          MobileStore
        </div>

        {/* MENU */}
        <nav className="nav">
          <Link to="/">Trang ch·ªß</Link>
          <Link to="/products">S·∫£n ph·∫©m</Link>
          <Link to="/news">Tin t·ª©c</Link>
          <Link to="/contact">Li√™n h·ªá</Link>
        </nav>

        {/* ACTION */}
        <div className="actions">
          <div
            className="cart"
            onClick={() => navigate("/cart")}
          >
            üõí
            {cartCount > 0 && <span className="badge">{cartCount}</span>}
          </div>

          {!user ? (
            <button
              className="btn-login"
              onClick={() => navigate("/login")}
            >
              ƒêƒÉng nh·∫≠p
            </button>
          ) : (
            <div className="user">
              <span onClick={() => setOpenMenu(!openMenu)}>
                üë§ {user.name || "User"}
              </span>

              {openMenu && (
                <div className="user-menu">
                  <div onClick={() => navigate("/profile")}>
                    T√†i kho·∫£n
                  </div>
                  <div onClick={logout}>ƒêƒÉng xu·∫•t</div>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </header>
  );
}
