import React, { useState, useEffect } from "react";
import "./PageHeader.css";
import { useNavigate } from "react-router-dom";

export default function PageHeader({ user }) {
  const navigate = useNavigate();

  const [openMenu, setOpenMenu] = useState(false);

  // ‚≠ê state ri√™ng cho cart
  const [cartCount, setCartCount] = useState(0);

  const toggleMenu = () => setOpenMenu(!openMenu);

  const handleLogout = () => {
    localStorage.removeItem("trip-token");
    navigate("/login");
    window.location.reload();
  };

  // ‚≠ê c·∫≠p nh·∫≠t s·ªë l∆∞·ª£ng gi·ªè m·ªói khi user thay ƒë·ªïi ho·∫∑c storage thay ƒë·ªïi
  useEffect(() => {
    if (!user) {
      setCartCount(0);
      return;
    }

    const key = `cart_${user.email}`;
    const updateCartCount = () => {
      const cart = JSON.parse(localStorage.getItem(key)) || [];
      const total = cart.reduce((sum, item) => sum + item.quantity, 0);
      setCartCount(total);
    };

    // ch·∫°y 1 l·∫ßn
    updateCartCount();

    // l·∫Øng nghe thay ƒë·ªïi storage (th√™m, xo√°, tƒÉng gi·∫£m gi·ªè)
    window.addEventListener("storage", updateCartCount);

    return () => {
      window.removeEventListener("storage", updateCartCount);
    };
  }, [user]);

  const goToCart = () => {
    if (!user) {
      alert("B·∫°n c·∫ßn ƒëƒÉng nh·∫≠p ƒë·ªÉ xem gi·ªè h√†ng!");
      navigate("/login");
      return;
    }
    navigate("/cart");
  };

  return (
    <>
      {/* TOP MINI BAR */}
      <div className="cp-topbar">
        <div className="cp-content d-flex justify-content-between">
          <div className="small text-white">Hotline: 1800-2097</div>
          <div className="small d-none d-md-block text-white">
            Giao nhanh trong 2 gi·ªù ‚úà
          </div>
        </div>
      </div>

      {/* MAIN HEADER */}
      <div className="cp-header shadow-sm">
        <div className="cp-content d-flex align-items-center">

          {/* LOGO */}
          <div className="cp-logo" onClick={() => navigate("/")}>
            Cellphone<span style={{ color:"#d70018" }}>S</span>
          </div>

          {/* SEARCH */}
          <div className="flex-grow-1 px-3">
            <input 
              className="form-control cp-search"
              placeholder="B·∫°n mu·ªën t√¨m g√¨ h√¥m nay?"
            />
          </div>

          {/* ACTIONS */}
          <div className="d-flex align-items-center gap-4">

            {/* USER */}
            {user ? (
              <div className="position-relative cp-user-wrap">

                {/* Avatar FIX - gi·ªØ nguy√™n */}
                <div
                  onClick={toggleMenu}
                  className="rounded-circle cp-avatar d-flex justify-content-center align-items-center"
                  style={{
                    width: 38,
                    height: 38,
                    background: "#eee",
                    overflow: "hidden",
                    cursor: "pointer",
                    fontWeight: 600
                  }}
                >
                  {user.avatar && user.avatar.trim() !== "" ? (
                    <img
                      src={user.avatar}
                      alt="avatar"
                      style={{ width: "100%", height: "100%", objectFit: "cover" }}
                    />
                  ) : (
                    user.fullName?.charAt(0)?.toUpperCase() || "U"
                  )}
                </div>

                {openMenu && (
                  <div className="cp-user-menu shadow">
                    <div onClick={() => navigate("/profile")}>üë§ Th√¥ng tin</div>
                    <div onClick={() => navigate("/my-orders")}>üì¶ ƒê∆°n h√†ng</div>
                    <div onClick={handleLogout}>üö™ ƒêƒÉng xu·∫•t</div>
                  </div>
                )}
              </div>
            ) : (
              <button
                className="btn btn-sm btn-danger"
                onClick={() => navigate("/login")}
              >
                ƒêƒÉng nh·∫≠p
              </button>
            )}

            {/* CART */}
            <div className="position-relative cp-cart-icon" onClick={goToCart}>
              üõí
              {cartCount > 0 && (
                <span className="cp-cart-count">{cartCount}</span>
              )}
            </div>

          </div>

        </div>
      </div>
    </>
  );
}
