import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../css/page/HomePage.css";
import ProfileHeader from "../components/page/PageHeader";
import { DataContext } from "../DataContext";

export default function HomePage() {
  const [products, setProducts] = useState([]);
  const { token } = useContext(DataContext);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get("http://localhost:8080/api/products", {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(res => setProducts(res.data.data))
    .catch(err => console.log(err));
  }, [token]);

  return (
    <div className="homepage-container">
      
      <ProfileHeader />

      <section className="hp-wrapper">

        <h2 className="hp-title">Sản phẩm nổi bật</h2>

        <div className="hp-grid">
          {products.map(p => (
            <div key={p.id} className="hp-card">
              
              <img
                src={p.images[0]?.imageUrl}
                alt={p.name}
                className="hp-img"
                onClick={() => navigate(`/product/${p.id}`)}
              />

              <div className="hp-info">
                <h5 className="hp-name">{p.name}</h5>
                <p className="hp-brand">{p.brand}</p>

                <div className="hp-price">
                  {p.price.toLocaleString()} ₫
                </div>
              </div>
            </div>
          ))}
        </div>

      </section>
    </div>
  );
}
