import React, { useContext, useState, useEffect } from "react";
import axios from "axios";
import { toast, ToastContainer } from "react-toastify";
import { useNavigate } from "react-router-dom";
import { DataContext } from "../DataContext";

function ChangePasswordPage() {
  const { token, user } = useContext(DataContext);
  const navigate = useNavigate();

  const [form, setForm] = useState({
    email: "",
    oldPassword: "",
    newPassword: "",
    confirmPassword: "",
  });

  const [loading, setLoading] = useState(false);

  // üî• N·∫øu ƒë√£ login th√¨ t·ª± ƒë·ªï email v√†o input
  useEffect(() => {
    if (user && user.email) {
      setForm((prev) => ({
        ...prev,
        email: user.email,
      }));
    }
  }, [user]);

  const handleChangeInput = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.email) {
      toast.error("B·∫°n ch∆∞a ƒëƒÉng nh·∫≠p!");
      return;
    }

    if (form.newPassword !== form.confirmPassword) {
      toast.error("M·∫≠t kh·∫©u x√°c nh·∫≠n kh√¥ng kh·ªõp!");
      return;
    }

    setLoading(true);

    try {
      const res = await axios.post(
        "http://localhost:8080/api/auth/change-password",
        {
          email: form.email,
          oldPassword: form.oldPassword,
          newPassword: form.newPassword,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (res.status === 200) {
        toast.success("ƒê·ªïi m·∫≠t kh·∫©u th√†nh c√¥ng!");
        setTimeout(() => {
          navigate("/profile");
        }, 800);
      }
    } catch (err) {
      console.log("error: ", err);
      toast.error(err.response?.data?.message || "ƒê·ªïi m·∫≠t kh·∫©u th·∫•t b·∫°i!");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      className="bg-light min-vh-100 d-flex justify-content-center align-items-center"
      style={{ background: "#fffaf5" }}
    >
      <div className="col-md-5 col-lg-4">
        <div className="card border-0 shadow-lg rounded-4">
          <div className="card-body p-5">
            <h3
              className="text-center mb-4 fw-bold"
              style={{ color: "#ff7a29" }}
            >
              Change Password
            </h3>

            <form onSubmit={handleSubmit}>

              {/* Email t·ª± g·∫Øn + kh√¥ng cho s·ª≠a */}
              <div className="mb-3">
                <label className="form-label fw-semibold">Email</label>
                <input
                  type="email"
                  name="email"
                  className="form-control rounded-pill px-3"
                  value={form.email}
                  readOnly
                />
              </div>

              <div className="mb-3">
                <label className="form-label fw-semibold">Old Password</label>
                <input
                  type="password"
                  name="oldPassword"
                  className="form-control rounded-pill px-3"
                  placeholder="Enter old password"
                  value={form.oldPassword}
                  onChange={handleChangeInput}
                  required
                />
              </div>

              <div className="mb-3">
                <label className="form-label fw-semibold">New Password</label>
                <input
                  type="password"
                  name="newPassword"
                  className="form-control rounded-pill px-3"
                  placeholder="Enter new password"
                  value={form.newPassword}
                  onChange={handleChangeInput}
                  required
                />
              </div>

              <div className="mb-4">
                <label className="form-label fw-semibold">
                  Confirm Password
                </label>
                <input
                  type="password"
                  name="confirmPassword"
                  className="form-control rounded-pill px-3"
                  placeholder="Confirm new password"
                  value={form.confirmPassword}
                  onChange={handleChangeInput}
                  required
                />
              </div>

              <div className="d-grid">
                <button
                  type="submit"
                  className="btn btn-lg text-white rounded-pill shadow"
                  style={{
                    background: "linear-gradient(135deg, #ff7a29, #ff9e57)",
                    border: "none",
                  }}
                  disabled={loading}
                >
                  {loading ? "Processing..." : "Change Password"}
                </button>
              </div>
            </form>
          </div>
        </div>

        <ToastContainer />
      </div>
    </div>
  );
}

export default ChangePasswordPage;
