// src/pages/MyOrdersPage.js
import React, { useEffect, useState } from "react";
import "../css/oders/MyOrdersPage.css";
import axios from "axios";

import PageHeader from "../components/page/PageHeader";
import { useNavigate } from "react-router-dom";
import ReviewModal from "../components/review/ReviewModal";

export default function MyOrdersPage() {
  const [orders, setOrders] = useState([]);
  const [user, setUser] = useState(null);

  // ⭐ state modal
  const [openReview, setOpenReview] = useState(false);
  const [reviewProductId, setReviewProductId] = useState(null);
  const [reviewProductName, setReviewProductName] = useState("");

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("trip-token");
    if (!token) {
      navigate("/login");
      return;
    }

    axios
      .get("http://localhost:8080/api/auth/user/me", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => {
        setUser(res.data.data);
      })
      .catch(() => {
        localStorage.removeItem("trip-token");
        navigate("/login");
      });

    axios
      .get("http://localhost:8080/api/orders/my", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => {
        setOrders(res.data || []);
      })
      .catch((err) => {
        if ([401, 403].includes(err.response?.status)) {
          localStorage.removeItem("trip-token");
          navigate("/login");
        }
      });
  }, [navigate]);

  // Hủy đơn
  const handleCancel = (id) => {
    const token = localStorage.getItem("trip-token");

    axios
      .put(
        `http://localhost:8080/api/orders/${id}/cancel`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      )
      .then(() => {
        alert("Hủy đơn thành công");
        setOrders((prev) =>
          prev.map((o) => (o.id === id ? { ...o, status: "CANCELLED" } : o))
        );
      })
      .catch(() => alert("Không thể hủy đơn"));
  };

  // ⭐ mở modal review
  const openReviewModal = (productId, productName) => {
    setReviewProductId(productId);
    setReviewProductName(productName);
    setOpenReview(true);
  };

  return (
    <>
      <PageHeader user={user} />

      <div className="order-container">
        <h2>Đơn hàng của bạn</h2>

        {orders.map((order) => (
          <div className="order-card" key={order.id}>
            <div className="order-header">
              <span className="order-id">Mã đơn #{order.id}</span>
              <span className={`order-status status-${order.status}`}>
                {translateStatus(order.status)}
              </span>
            </div>

            <div className="order-products">
              {order.items?.map((item) => (
                <div className="order-product" key={item.productId}>
                  <img
                    src={item.imageUrl || "https://via.placeholder.com/70"}
                    alt=""
                  />
                  <div>
                    <div className="name">{item.productName}</div>
                    <div className="qty">x{item.quantity}</div>
                    <div className="price">
                      {item.price.toLocaleString()} ₫
                    </div>

                    {/* ⭐ Nút đánh giá sản phẩm chỉ khi COMPLETED */}
                    {order.status === "COMPLETED" && (
                      <button
                        style={{ marginTop: "8px" }}
                        onClick={() =>
                          openReviewModal(item.productId, item.productName)
                        }
                      >
                        Đánh giá sản phẩm
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>

            <div className="order-footer">
              <div className="order-total">
                Tổng: {order.totalPrice.toLocaleString()} ₫
              </div>

              {(order.status === "PENDING" ||
                order.status === "CONFIRMED") && (
                <button
                  onClick={() => handleCancel(order.id)}
                  className="btn-cancel"
                >
                  Hủy đơn
                </button>
              )}
            </div>
          </div>
        ))}
      </div>

      {/* ⭐ Modal review */}
      <ReviewModal
        open={openReview}
        onClose={() => setOpenReview(false)}
        productId={reviewProductId}
        productName={reviewProductName}
      />
    </>
  );
}

function translateStatus(status) {
  switch (status) {
    case "PENDING":
      return "Chờ xác nhận";
    case "CONFIRMED":
      return "Đã xác nhận";
    case "SHIPPING":
      return "Đang giao";
    case "COMPLETED":
      return "Giao thành công";
    case "CANCELLED":
      return "Đã hủy";
    default:
      return status;
  }
}
