import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import "../css/page/HomePage.css";
import ProfileHeader from "../components/page/PageHeader";
import { DataContext } from "../DataContext";
import { useNavigate } from "react-router-dom";

export default function HomePage() {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();
  const { token } = useContext(DataContext);

  useEffect(() => {
    axios.get("http://localhost:8080/api/products", {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(res => setProducts(res.data.data))
  }, [token]);

  return (
    <div className="hp-container">

      <ProfileHeader />

      {/* BANNER */}
      <section className="hp-banner">
        <div className="hp-banner-slide">üî• Khuy·∫øn m√£i c·ª±c s·ªëc!</div>
      </section>

      {/* Category icon */}
      <section className="hp-category">
        <div className="hp-cat-item">üì± ƒêi·ªán tho·∫°i</div>
        <div className="hp-cat-item">üíª Laptop</div>
        <div className="hp-cat-item">üéß √Çm thanh</div>
        <div className="hp-cat-item">üì∫ TV</div>
      </section>

      {/* FLASH SALE */}
      <section className="hp-section">
        <h2>üî• FLASH SALE</h2>

        <div className="hp-scroll-row">
          {products.map(p => (
            <div key={p.id} className="hp-flash-card">
              <img src={p.images[0]?.imageUrl} alt={p.name}/>
              <div className="hp-name">{p.name}</div>
              <div className="hp-price">
                {p.price.toLocaleString()} ‚Ç´
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* PRODUCT GRID */}
      <section className="hp-section">
        <h2>G·ª£i √Ω h√¥m nay</h2>

        <div className="hp-grid">
          {products.map(p => (
            <div key={p.id} className="hp-card">
              <img src={p.images[0]?.imageUrl} alt={p.name}
                   onClick={() => navigate(`/product/${p.id}`)}
              />
              <div className="hp-info">
                <span className="hp-brand">{p.brand}</span>
                <h3>{p.name}</h3>
                <p>{p.price.toLocaleString()} ‚Ç´</p>
              </div>
            </div>
          ))}
        </div>
      </section>

    </div>
  );
}
