import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function ForgotPassword() {
  const [email, setEmail] = useState("");
  const [isSent, setIsSent] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
    const navigate = useNavigate();
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!email) {
      setError("Vui lòng nhập email!");
      return;
    }

    setLoading(true);
    setError("");

    try {
      const res = await axios.post(
        "http://localhost:8080/api/auth/send-reset-otp",
        {
          email: email,
        }
      );
      if (res.status == 200) {
        setIsSent(true);
        setTimeout(()=>{
            navigate("/verify-reset", { state: { email:email } })
        },2000)
      } else {
        console.log(res.data.message);

        setError("Có lỗi xảy ra!");
      }
    } catch (err) {
      setError(err.response?.data?.message || "Không thể kết nối server!");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container d-flex justify-content-center align-items-center vh-100">
      <div
        className="card shadow p-4"
        style={{ maxWidth: "400px", width: "100%" }}
      >
        <h3 className="text-center mb-4">Quên mật khẩu</h3>

        {isSent ? (
          <div className="alert alert-success text-center">
            ✅ Mã OTP đã được gửi tới email <strong>{email}</strong>. Vui lòng
            kiểm tra hộp thư để tiếp tục.
          </div>
        ) : (
          <form onSubmit={handleSubmit}>
            {error && <div className="alert alert-danger">{error}</div>}

            <div className="mb-3">
              <label className="form-label">Email của bạn</label>
              <input
                type="email"
                className="form-control"
                placeholder="Nhập email..."
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>

            <button
              type="submit"
              className="btn btn-primary w-100"
              disabled={loading}
            >
              {loading ? "Đang gửi..." : "Gửi mã OTP"}
            </button>
          </form>
        )}
      </div>
    </div>
  );
}
