import React, { useContext, useEffect } from "react";
import "../css/page/CartPage.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { DataContext } from "../DataContext";
import PageHeader from "../components/page/PageHeader";

export default function CartPage() {
  const navigate = useNavigate();
  const { user, token, cart, fetchCart } = useContext(DataContext);

  // SỬA TẠI ĐÂY: Ép tải lại giỏ hàng khi vào trang
  useEffect(() => {
    if (token) fetchCart();
  }, [token, fetchCart]);

  const updateQty = async (cartItemId, newQty) => {
    if (newQty < 1) return;
    try {
      await axios.put(`http://localhost:8080/api/cart/update/${cartItemId}?quantity=${newQty}`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchCart(); 
    } catch (err) { alert("Lỗi cập nhật!"); }
  };

  const removeItem = async (cartItemId) => {
    if (!window.confirm("Xóa sản phẩm này?")) return;
    try {
      await axios.delete(`http://localhost:8080/api/cart/${cartItemId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchCart();
    } catch (err) { alert("Lỗi xóa sản phẩm!"); }
  };

  const total = Array.isArray(cart) 
    ? cart.reduce((sum, i) => sum + ((i.product?.price || 0) * i.quantity), 0) 
    : 0;

  return (
    <div className="cart-container">
      <PageHeader />
      <div className="cart-main-content" style={{padding: '20px', maxWidth: '1200px', margin: '80px auto'}}>
        <h2>Giỏ hàng của bạn</h2>
        {(!cart || cart.length === 0) ? (
          <div className="empty-cart">
            <p>Giỏ hàng đang trống.</p>
            <button onClick={() => navigate("/products")}>Tiếp tục mua sắm</button>
          </div>
        ) : (
          <div className="cart-grid">
            <div className="cart-items">
              {cart.map((item) => (
                <div className="cart-item" key={item.id} style={{display: 'flex', gap: '20px', borderBottom: '1px solid #eee', padding: '15px 0'}}>
                  <img src={item.product?.imageUrls?.[0]} alt={item.product?.name} style={{width: '100px', height: '100px', objectFit: 'cover'}} />
                  <div className="info" style={{flex: 1}}>
                    <div className="name" style={{fontWeight: 'bold'}}>{item.product?.name}</div>
                    <div className="price">{item.product?.price?.toLocaleString()} ₫</div>
                    <div className="qty-box" style={{margin: '10px 0'}}>
                      <button onClick={() => updateQty(item.id, item.quantity - 1)}>-</button>
                      <span style={{margin: '0 15px'}}>{item.quantity}</span>
                      <button onClick={() => updateQty(item.id, item.quantity + 1)}>+</button>
                    </div>
                    <button className="remove" onClick={() => removeItem(item.id)} style={{color: 'red', border: 'none', background: 'none', cursor: 'pointer'}}>Xóa</button>
                  </div>
                  <div className="subtotal" style={{fontWeight: 'bold'}}>
                    {((item.product?.price || 0) * item.quantity).toLocaleString()} ₫
                  </div>
                </div>
              ))}
            </div>

            <div className="cart-summary" style={{padding: '20px', background: '#f9f9f9', borderRadius: '8px', height: 'fit-content'}}>
              <h3>Tóm tắt đơn hàng</h3>
              <div className="sum-row" style={{display: 'flex', justifyContent: 'space-between', margin: '20px 0'}}>
                <span>Tổng cộng:</span>
                <strong style={{fontSize: '1.2rem', color: '#2563eb'}}>{total.toLocaleString()} ₫</strong>
              </div>
              <button className="btn-checkout" onClick={() => navigate("/checkout")} style={{width: '100%', padding: '12px', background: '#2563eb', color: '#fff', border: 'none', borderRadius: '4px', cursor: 'pointer'}}>
                Thanh toán ngay
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
