import React, { useEffect, useState, useContext, useMemo } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

import "../css/page/ProductDetail.css";
import ProfileHeader from "../components/page/PageHeader";
import { DataContext } from "../DataContext";

export default function ProductDetail() {
  const { id } = useParams();
  const navigate = useNavigate();

  const { token, setToken, fetchCart } = useContext(DataContext);

  const [product, setProduct] = useState(null);
  const [user, setUser] = useState(null);
  const [reviews, setReviews] = useState([]);

  // gallery
  const [activeImage, setActiveImage] = useState(0);

  // review filter
  const [starFilter, setStarFilter] = useState(0);
  const [hasCommentOnly, setHasCommentOnly] = useState(false);
  const [sortType, setSortType] = useState("newest");

  /* ================= LOAD PRODUCT ================= */
  useEffect(() => {
    axios
      .get(`http://localhost:8080/api/products/${id}`)
      .then((res) => {
        const p = res?.data?.data ?? res?.data ?? null;

        if (!p || p.status !== "ACTIVE") {
          alert("S·∫£n ph·∫©m kh√¥ng c√≤n kinh doanh!");
          navigate("/");
          return;
        }

        setProduct(p);
        setActiveImage(0);
      })
      .catch(() => navigate("/"));
  }, [id, navigate]);

  /* ================= LOAD REVIEWS ================= */
  useEffect(() => {
    axios
      .get(`http://localhost:8080/api/reviews/product/${id}`)
      .then((res) => {
        const payload = res?.data;
        let list = [];

        if (Array.isArray(payload)) list = payload;
        else if (Array.isArray(payload?.data)) list = payload.data;
        else if (Array.isArray(payload?.reviews)) list = payload.reviews;

        setReviews(list);
      })
      .catch(() => setReviews([]));
  }, [id]);

  /* ================= LOAD USER ================= */
  useEffect(() => {
    const localToken = localStorage.getItem("trip-token");
    if (!localToken) {
      setUser(null);
      return;
    }

    axios
      .get("http://localhost:8080/api/auth/user/me", {
        headers: { Authorization: `Bearer ${localToken}` },
      })
      .then((res) => {
        setUser(res?.data?.data ?? res?.data ?? null);
      })
      .catch(() => setUser(null));
  }, [token]);

  /* ================= CART ================= */
const addToCart = async () => {
  if (!user) {
    alert("B·∫°n c·∫ßn ƒëƒÉng nh·∫≠p ƒë·ªÉ th√™m v√†o gi·ªè!");
    navigate("/login");
    return;
  }

  if (product.quantityInStock === 0) {
    alert("S·∫£n ph·∫©m ƒë√£ h·∫øt h√†ng!");
    return;
  }

  try {
    // G·ªçi API gi·ªëng h·ªát trang Home
    await axios.post(
      `http://localhost:8080/api/cart/add?productId=${id}&quantity=1`,
      {},
      { headers: { Authorization: `Bearer ${token}` } }
    );
    
    // C·∫≠p nh·∫≠t l·∫°i Context (s·ªë l∆∞·ª£ng tr√™n Header)
    if (typeof fetchCart === 'function') {
      fetchCart();
    }
    
    alert("ƒê√£ th√™m v√†o gi·ªè h√†ng üõí");
  } catch (err) {
    console.error(err);
    alert("Kh√¥ng th·ªÉ th√™m v√†o gi·ªè h√†ng!");
  }
};

  const buyNow = () => {
    addToCart();
    navigate("/cart");
  };

  /* ================= RATING STATS ================= */
  const ratingStats = useMemo(() => {
    const total = reviews.length;
    if (total === 0)
      return { avg: 0, total: 0, bars: [0, 0, 0, 0, 0] };

    const bars = [0, 0, 0, 0, 0];
    let sum = 0;

    reviews.forEach((r) => {
      const rt = Number(r.rating) || 0;
      sum += rt;
      if (rt >= 1 && rt <= 5) bars[rt - 1]++;
    });

    return { avg: sum / total, total, bars };
  }, [reviews]);

  /* ================= FILTER REVIEWS ================= */
  const filteredReviews = useMemo(() => {
    let list = [...reviews];

    if (starFilter > 0)
      list = list.filter((r) => Number(r.rating) === starFilter);

    if (hasCommentOnly)
      list = list.filter((r) => r.comment?.trim());

    list.sort((a, b) => {
      const aD = new Date(a.createdAt || 0);
      const bD = new Date(b.createdAt || 0);
      switch (sortType) {
        case "oldest":
          return aD - bD;
        case "highest":
          return b.rating - a.rating;
        case "lowest":
          return a.rating - b.rating;
        default:
          return bD - aD;
      }
    });

    return list;
  }, [reviews, starFilter, hasCommentOnly, sortType]);

  const renderStars = (rating) =>
    "‚≠ê".repeat(Math.round(rating || 0)) +
    "‚òÜ".repeat(5 - Math.round(rating || 0));

  if (!product)
    return (
      <div className="loading">
        <div className="spinner-border text-danger"></div>
      </div>
    );


const renderAvatar = (name, url, size = 44) => {
    const firstLetter = name ? name.charAt(0).toUpperCase() : "U";
    return (
      <div className="avatar-circle" style={{ 
        width: size, 
        height: size, 
        backgroundColor: url ? 'transparent' : '#3182ce',
        flexShrink: 0,
        borderRadius: '50%',
        overflow: 'hidden',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#fff',
        fontWeight: 'bold'
      }}>
        {url ? (
          <img src={url} alt={name} style={{width: '100%', height: '100%', objectFit: 'cover'}} 
               onError={(e) => { e.target.src = ""; e.target.parentElement.style.backgroundColor = "#3182ce"; }} />
        ) : (
          <span>{firstLetter}</span>
        )}
      </div>
    );
  };

  return (
    <div className="pd-container">
      <ProfileHeader
        user={user}
        onLogout={() => {
          localStorage.removeItem("trip-token");
          setToken(null);
          setUser(null);
          window.location.href = "/";
        }}
      />

      {/* ================= PRODUCT ================= */}
      <div className="pd-content">
        {/* LEFT */}
        <div className="pd-left">
          <div className="pd-main-image">
            {product.quantityInStock === 0 && (
              <div className="pd-out-stock">H·∫æT H√ÄNG</div>
            )}
            <img
              src={product.imageUrls?.[activeImage]}
              alt={product.name}
            />
          </div>

          <div className="pd-thumb-list">
            {product.imageUrls?.map((url, i) => (
              <img
                key={i}
                src={url}
                alt={`thumb-${i}`}
                className={i === activeImage ? "active" : ""}
                onClick={() => setActiveImage(i)}
              />
            ))}
          </div>
        </div>

        {/* RIGHT */}
        <div className="pd-right">
          <h2 className="pd-name">{product.name}</h2>

          <div className="pd-rating-line">
            <span className="pd-rating-score">
              {ratingStats.avg.toFixed(1)}
            </span>
            <span className="pd-rating-stars">
              {renderStars(ratingStats.avg)}
            </span>
            <span className="pd-rating-count">
              ({ratingStats.total} ƒë√°nh gi√°)
            </span>
          </div>

          <span className="pd-price">
            {product.price.toLocaleString()} ‚Ç´
          </span>

          <div className="pd-actions">
            <button
              className="pd-buy"
              onClick={buyNow}
              disabled={product.quantityInStock === 0}
            >
              Mua ngay
            </button>
            <button
              className="pd-add"
              onClick={addToCart}
              disabled={product.quantityInStock === 0}
            >
              Th√™m v√†o gi·ªè
            </button>
          </div>

          <ul className="pd-spec">
  <li>üß† Chip: {product.chipset}</li>
  <li>üîã Pin: {product.battery}</li>
  <li>üíæ RAM: {product.ram}</li>
  <li>üì¶ B·ªô nh·ªõ: {product.storage}</li>
  <li>üé® M√†u: {product.color}</li>
  <li>ü§ñ H·ªá ƒëi·ªÅu h√†nh: {product.os}</li>
  <li>üì± M√†n h√¨nh: {product.screenSize}</li>
  <li>üì∑ Camera: {product.camera}</li>
</ul>
        </div>
      </div>

      {/* ================= DESCRIPTION ================= */}
      <div className="pd-desc">
        <h3>M√¥ t·∫£ s·∫£n ph·∫©m</h3>
        <p>{product.description}</p>
      </div>

      {/* ================= REVIEW ================= */}
      <div className="pd-review-section">
        <h3>ƒê√°nh gi√° s·∫£n ph·∫©m</h3>

        <div className="pd-rating-box">
          <div className="pd-rating-left">
            <div className="pd-rating-number">
              {ratingStats.avg.toFixed(1)}
            </div>
            <div className="pd-stars-big">
              {renderStars(ratingStats.avg)}
            </div>
            <div className="pd-total">
              {ratingStats.total} ƒë√°nh gi√°
            </div>
          </div>

          <div className="pd-rating-right">
            {[5,4,3,2,1].map((s) => (
              <div key={s} className="pd-bar">
                <span>{s} sao</span>
                <div className="pd-bg">
                  <div
                    className="pd-fill"
                    style={{
                      width: ratingStats.total
                        ? `${(ratingStats.bars[s-1]/ratingStats.total)*100}%`
                        : "0%",
                    }}
                  ></div>
                </div>
                <span className="pd-bar-count">
                  {ratingStats.bars[s-1]}
                </span>
              </div>
            ))}
          </div>
        </div>

        <div className="pd-filter-row">
          <div className="pd-filter-stars">
            <button
              className={"pd-filter-btn " + (starFilter === 0 ? "active" : "")}
              onClick={() => setStarFilter(0)}
            >
              T·∫•t c·∫£
            </button>

            {[5,4,3,2,1].map((s) => (
              <button
                key={s}
                className={"pd-filter-btn " + (starFilter === s ? "active" : "")}
                onClick={() => setStarFilter(s)}
              >
                {s} sao
              </button>
            ))}

            <button
              className={"pd-filter-btn " + (hasCommentOnly ? "active" : "")}
              onClick={() => setHasCommentOnly((v) => !v)}
            >
              C√≥ b√¨nh lu·∫≠n
            </button>
          </div>

          <div className="pd-sort">
            <span>S·∫Øp x·∫øp:</span>
            <select
              value={sortType}
              onChange={(e) => setSortType(e.target.value)}
            >
              <option value="newest">M·ªõi nh·∫•t</option>
              <option value="oldest">C≈© nh·∫•t</option>
              <option value="highest">Sao cao</option>
              <option value="lowest">Sao th·∫•p</option>
            </select>
          </div>
        </div>

        <div className="pd-review-list">
          {filteredReviews.length === 0 && (
            <p className="pd-no-review">
              Ch∆∞a c√≥ ƒë√°nh gi√° ph√π h·ª£p v·ªõi b·ªô l·ªçc.
            </p>
          )}

          <div className="pd-review-header" style={{ display: 'flex', gap: '12px', alignItems: 'center', marginBottom: '12px' }}>
  {/* L·∫•y Avatar t·ª´ r.user.avatar gi·ªëng trang News */}
  {renderAvatar(r.user?.fullName || r.userName, r.user?.avatar, 48)}
  
  <div>
    <div className="pd-review-name" style={{ fontWeight: 'bold', color: '#2d3748' }}>
      {r.user?.fullName || r.userName || "Ng∆∞·ªùi d√πng"}
    </div>
    <div className="pd-review-stars" style={{ fontSize: '12px' }}>
      {"‚≠ê".repeat(r.rating || 0)}
    </div>
    <div className="pd-review-date" style={{ fontSize: '13px', color: '#718096' }}>
      {/* L·∫•y ng√†y t·ª´ database r.createdAt */}
      {r.createdAt ? new Date(r.createdAt).toLocaleDateString("vi-VN") : "V·ª´a xong"}
    </div>
  </div>
</div>
        </div>
      </div>
    </div>
  );
}
