import { createContext, useState, useEffect, useCallback } from "react";
import axios from "axios";

export const DataContext = createContext();

export const DataProvider = ({ children }) => {
  const [token, setToken] = useState(localStorage.getItem("trip-token"));
  const [user, setUser] = useState(null);
  const [loadingUser, setLoadingUser] = useState(true);
  
  // Trạng thái giỏ hàng dùng chung toàn ứng dụng
  const [cart, setCart] = useState([]);

  /* ===== LƯU TOKEN ===== */
  useEffect(() => {
    if (token) {
      localStorage.setItem("trip-token", token);
    } else {
      localStorage.removeItem("trip-token");
      setUser(null);
      setCart([]); // Xóa giỏ hàng khi logout
    }
  }, [token]);

  /* ===== HÀM LẤY GIỎ HÀNG TỪ DATABASE ===== */
  const fetchCart = useCallback(async () => {
    if (!token || !user) return;
    try {
      const res = await axios.get("http://localhost:8080/api/cart", {
        headers: { Authorization: `Bearer ${token}` },
      });
      // Giả sử API trả về mảng các CartItem
      setCart(res.data || []);
    } catch (err) {
      console.error("Fetch cart failed:", err);
    }
  }, [token, user]);

  /* ===== LOAD USER TỪ TOKEN ===== */
  useEffect(() => {
    const fetchUser = async () => {
      if (!token) {
        setLoadingUser(false);
        return;
      }

      try {
        const res = await axios.get("http://localhost:8080/api/auth/user/me", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setUser(res.data.data || res.data);
      } catch (err) {
        console.error("Load user failed:", err);
        setToken(null);
        setUser(null);
      } finally {
        setLoadingUser(false);
      }
    };

    fetchUser();
  }, [token]);

  /* ===== TỰ ĐỘNG LOAD GIỎ HÀNG KHI CÓ USER ===== */
  useEffect(() => {
    if (user) {
      fetchCart();
    }
  }, [user, fetchCart]);

  /* ===== LOGOUT ===== */
  const logout = () => {
    localStorage.removeItem("trip-token");
    localStorage.removeItem("user-data");
    setToken(null);
    setUser(null);
    setCart([]);
  };

  return (
    <DataContext.Provider
      value={{
        token,
        setToken,
        user,
        setUser,
        logout,
        loadingUser,
        cart,      // Export cart để các component khác sử dụng
        setCart,   // Cho phép cập nhật trực tiếp nếu cần
        fetchCart, // Export hàm này để gọi lại sau khi Add/Delete
      }}
    >
      {children}
    </DataContext.Provider>
  );
};
