// src/pages/MyOrdersPage.js
import React, { useEffect, useState } from "react";
import "../css/oders/MyOrdersPage.css";
import axios from "axios";

import PageHeader from "../components/page/PageHeader";
import { useNavigate } from "react-router-dom";

export default function MyOrdersPage() {
  const [orders, setOrders] = useState([]);
  const [user, setUser] = useState(null);

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("trip-token");
    if (!token) {
      navigate("/login");
      return;
    }

    // üî• l·∫•y user
    axios.get("http://localhost:8080/api/auth/user/me", {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then((res) => {
        console.log("USER RESPONSE:", res.data);

        // ‚≠ê l·∫•y ƒë√∫ng `data`
        setUser(res.data.data);
      })
      .catch(() => {
        localStorage.removeItem("trip-token");
        navigate("/login");
      });

    // üî• l·∫•y ƒë∆°n h√†ng
    axios.get("http://localhost:8080/api/orders/my", {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then((res) => {
        console.log("My Orders:", res.data);
        setOrders(res.data || []);
      })
      .catch((err) => {
        console.log("Error:", err.response?.data || err.message);

        if ([401, 403].includes(err.response?.status)) {
          localStorage.removeItem("trip-token");
          navigate("/login");
        }
      });

  }, []);

  // H·ªßy ƒë∆°n
  const handleCancel = (id) => {
    const token = localStorage.getItem("trip-token");

    axios.put(
      `http://localhost:8080/api/orders/${id}/cancel`,
      {},
      { headers: { Authorization: `Bearer ${token}` } }
    )
      .then(() => {
        alert("H·ªßy ƒë∆°n th√†nh c√¥ng");
        setOrders((prev) =>
          prev.map((o) =>
            o.id === id ? { ...o, status: "CANCELLED" } : o
          )
        );
      })
      .catch(() => alert("Kh√¥ng th·ªÉ h·ªßy ƒë∆°n"));
  };

  return (
    <>
      <PageHeader user={user} />

      <div className="order-container">
        <h2>ƒê∆°n h√†ng c·ªßa b·∫°n</h2>

        {orders.map((order) => (
          <div className="order-card" key={order.id}>
            <div className="order-header">
              <span className="order-id">M√£ ƒë∆°n #{order.id}</span>
              <span className={`order-status status-${order.status}`}>
                {translateStatus(order.status)}
              </span>
            </div>

            <div className="order-products">
              {order.items?.map((item) => (
                <div className="order-product" key={item.productId}>
                  <img
                    src={item.imageUrl || "https://via.placeholder.com/70"}
                    alt=""
                  />
                  <div>
                    <div className="name">{item.productName}</div>
                    <div className="qty">x{item.quantity}</div>
                    <div className="price">
                      {item.price.toLocaleString()} ‚Ç´
                    </div>
                  </div>
                </div>
              ))}
            </div>

            <div className="order-footer">
              <div className="order-total">
                T·ªïng: {order.totalPrice.toLocaleString()} ‚Ç´
              </div>

              {(order.status === "PENDING" ||
                order.status === "CONFIRMED") && (
                <button
                  onClick={() => handleCancel(order.id)}
                  className="btn-cancel"
                >
                  H·ªßy ƒë∆°n
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
    </>
  );
}


function translateStatus(status) {
  switch (status) {
    case "PENDING": return "Ch·ªù x√°c nh·∫≠n";
    case "CONFIRMED": return "ƒê√£ x√°c nh·∫≠n";
    case "SHIPPING": return "ƒêang giao";
    case "COMPLETED": return "Giao th√†nh c√¥ng";
    case "CANCELLED": return "ƒê√£ h·ªßy";
    default: return status;
  }
}
