// src/pages/MyOrdersPage.js
import React, { useEffect, useState } from "react";
import "../css/oders/MyOrdersPage.css";
import axios from "axios";

import PageHeader from "../components/page/PageHeader";
import { useNavigate } from "react-router-dom";
import ReviewModal from "../components/review/ReviewModal";

export default function MyOrdersPage() {
  const [orders, setOrders] = useState([]);
  const [user, setUser] = useState(null);

  // ‚≠ê state modal
  const [openReview, setOpenReview] = useState(false);
  const [reviewProductId, setReviewProductId] = useState(null);
  const [reviewProductName, setReviewProductName] = useState("");

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("trip-token");
    if (!token) {
      navigate("/login");
      return;
    }

    axios
      .get("http://localhost:8080/api/auth/user/me", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => {
        setUser(res.data.data);
      })
      .catch(() => {
        localStorage.removeItem("trip-token");
        navigate("/login");
      });

    axios
      .get("http://localhost:8080/api/orders/my", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => {
        // ‚ú® th√™m field reviewed=false cho t·∫•t c·∫£ item
        const data = (res.data || []).map(order => ({
          ...order,
          items: order.items?.map(i => ({
            ...i,
            reviewed: false       // m·∫∑c ƒë·ªãnh
          }))
        }));
        setOrders(data);
      })
      .catch((err) => {
        if ([401, 403].includes(err.response?.status)) {
          localStorage.removeItem("trip-token");
          navigate("/login");
        }
      });
  }, [navigate]);

  // H·ªßy ƒë∆°n
  const handleCancel = (id) => {
    const token = localStorage.getItem("trip-token");

    axios
      .put(
        `http://localhost:8080/api/orders/${id}/cancel`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      )
      .then(() => {
        alert("H·ªßy ƒë∆°n th√†nh c√¥ng");
        setOrders((prev) =>
          prev.map((o) => (o.id === id ? { ...o, status: "CANCELLED" } : o))
        );
      })
      .catch(() => alert("Kh√¥ng th·ªÉ h·ªßy ƒë∆°n"));
  };

  // ‚≠ê m·ªü modal review
  const openReviewModal = (productId, productName) => {
    setReviewProductId(productId);
    setReviewProductName(productName);
    setOpenReview(true);
  };

  // ‚≠ê khi backend b√°o ƒë√£ ƒë√°nh gi√° ho·∫∑c qu√° 24h
  const markAsReviewed = (productId) => {
    setOrders(prev =>
      prev.map(order => ({
        ...order,
        items: order.items?.map(item =>
          item.productId === productId
            ? { ...item, reviewed: true }
            : item
        )
      }))
    );
  };

  return (
    <>
      <PageHeader user={user} />

      <div className="order-container">
        <h2>ƒê∆°n h√†ng c·ªßa b·∫°n</h2>

        {orders.map((order) => (
          <div className="order-card" key={order.id}>
            <div className="order-header">
              <span className="order-id">M√£ ƒë∆°n #{order.id}</span>
              <span className={`order-status status-${order.status}`}>
                {translateStatus(order.status)}
              </span>
            </div>

            <div className="order-products">
              {order.items?.map((item) => (
                <div className="order-product" key={item.productId}>
                  <img
                    src={item.imageUrl || "https://via.placeholder.com/70"}
                    alt=""
                  />
                  <div>
                    <div className="name">{item.productName}</div>
                    <div className="qty">x{item.quantity}</div>
                    <div className="price">
                      {item.price.toLocaleString()} ‚Ç´
                    </div>

                    {/* ‚≠ê N√∫t ƒë√°nh gi√° s·∫£n ph·∫©m ch·ªâ khi COMPLETED */}
                    {order.status === "COMPLETED" && (
                      <>
                        {item.reviewed ? (
                          <button
                            disabled
                            style={{
                              marginTop: 8,
                              background: "#ccc",
                              opacity: 0.7,
                              cursor: "not-allowed"
                            }}
                          >
                            ƒê√£ ƒë√°nh gi√°
                          </button>
                        ) : (
                          <button
                            style={{ marginTop: "8px" }}
                            onClick={() =>
                              openReviewModal(item.productId, item.productName)
                            }
                          >
                            ƒê√°nh gi√° s·∫£n ph·∫©m
                          </button>
                        )}
                      </>
                    )}
                  </div>
                </div>
              ))}
            </div>

            <div className="order-footer">
              <div className="order-total">
                T·ªïng: {order.totalPrice.toLocaleString()} ‚Ç´
              </div>

              {(order.status === "PENDING" ||
                order.status === "CONFIRMED") && (
                <button
                  onClick={() => handleCancel(order.id)}
                  className="btn-cancel"
                >
                  H·ªßy ƒë∆°n
                </button>
              )}
            </div>
          </div>
        ))}
      </div>

      {/* ‚≠ê Modal review */}
      <ReviewModal
        open={openReview}
        onClose={() => setOpenReview(false)}
        productId={reviewProductId}
        productName={reviewProductName}
        // üëâ callback FE nh·∫≠n khi backend tr·∫£ l·ªói ƒë√£ ƒë√°nh gi√° ho·∫∑c qu√° 24h
        onReviewed={markAsReviewed}
      />
    </>
  );
}

function translateStatus(status) {
  switch (status) {
    case "PENDING": return "Ch·ªù x√°c nh·∫≠n";
    case "CONFIRMED": return "ƒê√£ x√°c nh·∫≠n";
    case "SHIPPING": return "ƒêang giao";
    case "COMPLETED": return "Giao th√†nh c√¥ng";
    case "CANCELLED": return "ƒê√£ h·ªßy";
    default: return status;
  }
}
