import React, { useEffect, useState } from "react";
import axios from "axios";
import "../../css/admin/page/AdminOrdersPage.css";
import AdminHeader from "./AdminHeader";

export default function AdminOrdersPage() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    loadOrders();
  }, []);

  const loadOrders = async () => {
    const token = localStorage.getItem("adminToken");

    try {
      const res = await axios.get("http://localhost:8080/api/orders", {
        headers: { Authorization: `Bearer ${token}` },
      });

      setOrders(res.data || []);
    } catch (err) {
      console.log("ERR BACKEND =====> ", err.response?.data);
    }
  };

  const changeStatus = async (id, status) => {
    const token = localStorage.getItem("adminToken");

    await axios.put(
      `http://localhost:8080/api/orders/${id}/status`,
      { status },
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );

    loadOrders();
  };

  const renderStatus = (s) => {
    switch (s) {
      case "PENDING":
        return "‚è≥ Ch·ªù x√°c nh·∫≠n";
      case "CONFIRMED":
        return "‚úîÔ∏è ƒê√£ x√°c nh·∫≠n";
      case "SHIPPING":
        return "üöö ƒêang giao";
      case "COMPLETED":
        return "üéâ Giao th√†nh c√¥ng";
      case "CANCELLED":
        return "‚ùå ƒê√£ h·ªßy";
      default:
        return s;
    }
  };

  return (
    <>
      <AdminHeader />

      {/* ‚≠ê wrapper cƒÉn nh∆∞ header */}
      <div style={styles.wrapper}>
        <h2 style={styles.title}>Qu·∫£n l√Ω ƒë∆°n h√†ng</h2>

        <table style={styles.table}>
          <thead>
            <tr>
              <th>M√£ ƒë∆°n</th>
              <th>Kh√°ch h√†ng</th>
              <th>SƒêT</th>
              <th>ƒê·ªãa ch·ªâ</th>
              <th>T·ªïng ti·ªÅn</th>
              <th>Tr·∫°ng th√°i</th>
              <th>C·∫≠p nh·∫≠t</th>
            </tr>
          </thead>

          <tbody>
            {orders.map((o) => (
              <tr key={o.id}>
                <td>#{o.id}</td>
                <td>{o.fullName}</td>
                <td>{o.phone}</td>
                <td>{o.address}</td>
                <td>{o.totalPrice.toLocaleString()} ‚Ç´</td>

                <td>
                  <span className={`st-${o.status}`}>
                    {renderStatus(o.status)}
                  </span>
                </td>

                <td>
                  <select
                    value={o.status}
                    onChange={(e) => changeStatus(o.id, e.target.value)}
                    style={styles.select}
                  >
                    <option value="PENDING">Ch·ªù x√°c nh·∫≠n</option>
                    <option value="CONFIRMED">ƒê√£ x√°c nh·∫≠n</option>
                    <option value="SHIPPING">ƒêang giao</option>
                    <option value="COMPLETED">Ho√†n th√†nh</option>
                    <option value="CANCELLED">ƒê√£ h·ªßy</option>
                  </select>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
}

const styles = {
  wrapper: {
    maxWidth: 1350,
    margin: "80px auto",
    padding: 20,
    color: "#000",
    background: "#fff",
  },
  title: {
    marginBottom: 20,
    fontSize: 26,
    fontWeight: 600,
    color: "#000",
  },
  table: {
    width: "100%",
    background: "#fff",
    borderCollapse: "collapse",
    color: "#000",
  },
  select: {
    padding: "6px 8px",
    borderRadius: 6,
  },
};
