import React, { useEffect, useState, useContext } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import styles from "../../css/admin/HomePageAdmin";

import {
  Package,
  ShoppingBag,
  Tag,
  BarChart2,
  ChevronRight,
} from "lucide-react";

import AdminHeader from "./AdminHeader";
import { DataContext } from "../../DataContext";  // lấy token

export default function HomePageAdmin() {

  const { token } = useContext(DataContext);

  const [products, setProducts] = useState([]);
  const [discounts, setDiscounts] = useState([]);

  useEffect(() => {
    const config = token
      ? { headers: { Authorization: `Bearer ${token}` } }
      : {};

    axios.get("http://localhost:8080/api/products", config)
      .then(res => {
        const data = Array.isArray(res.data) ? res.data : res.data.data;
        setProducts(data || []);
      });

    axios.get("http://localhost:8080/api/discounts", config)
      .then(res => {
        const data = Array.isArray(res.data) ? res.data : res.data.data;
        setDiscounts(data || []);
      });

  }, [token]);

  return (
    <>
      <AdminHeader />
      <div style={{ height: 80 }}></div>

      <div style={styles.wrapper}>
        <div style={styles.container}>

          {/* QUẢN LÝ SẢN PHẨM  */}
          <div style={styles.card}>
            <div style={styles.cardHeader}>
              <h2 style={styles.cardTitle}>Quản Lý Sản Phẩm</h2>
              <Link to="/admin/products" style={styles.more}>Xem thêm</Link>
            </div>

            <div style={styles.row4}>
              <Link to="/admin/products" style={styles.itemBox}>
                <Package size={26} color="#ee4d2d" />
                <p>Số sản phẩm</p>
                <span>{products.length}</span>
              </Link>

              <Link to="/admin/products/add" style={styles.itemBox}>
                <Tag size={26} color="#3b82f6" />
                <p>Thêm sản phẩm</p>
                <span>Tạo mới</span>
              </Link>

              <Link to="/admin/discounts" style={styles.itemBox}>
                <ShoppingBag size={26} color="#a855f7" />
                <p>Mã giảm giá</p>
                <span>{discounts.length}</span>
              </Link>

            </div>
          </div>

        </div>
      </div>
    </>
  );
}
