import React, { useEffect, useState, useContext, useMemo } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import "../css/page/ProductDetail.css";
import ProfileHeader from "../components/page/PageHeader";
import { DataContext } from "../DataContext";

export default function ProductDetail() {
  const { id } = useParams();
  const navigate = useNavigate();

  const { token, setToken } = useContext(DataContext);

  const [product, setProduct] = useState(null);
  const [user, setUser] = useState(null);
  const [reviews, setReviews] = useState([]);

  const [starFilter, setStarFilter] = useState(0);
  const [hasCommentOnly, setHasCommentOnly] = useState(false);
  const [sortType, setSortType] = useState("newest");

  useEffect(() => {
    axios
      .get(`http://localhost:8080/api/products/${id}`)
      .then((res) => {
        const p = res.data.data;

        // n·∫øu s·∫£n ph·∫©m inactive
        if (p.status !== "ACTIVE") {
          alert("S·∫£n ph·∫©m kh√¥ng c√≤n kinh doanh!");
          navigate("/");
          return;
        }

        setProduct(p);
      })
      .catch(() => navigate("/"));
  }, [id, navigate]);

  useEffect(() => {
    axios
      .get(`http://localhost:8080/api/reviews/product/${id}`)
      .then((res) => setReviews(res.data || []))
      .catch(() => setReviews([]));
  }, [id]);

  useEffect(() => {
    const localToken = localStorage.getItem("trip-token");
    if (!localToken) {
      setUser(null);
      return;
    }

    axios
      .get("http://localhost:8080/api/auth/user/me", {
        headers: { Authorization: `Bearer ${localToken}` },
      })
      .then((res) => setUser(res.data.data))
      .catch(() => setUser(null));
  }, [token]);

  const addToCart = () => {
    const localToken = localStorage.getItem("trip-token");

    if (!localToken || !user) {
      alert("B·∫°n c·∫ßn ƒëƒÉng nh·∫≠p ƒë·ªÉ th√™m v√†o gi·ªè h√†ng!");
      navigate("/login");
      return;
    }

    if (product.quantityInStock === 0) {
      alert("S·∫£n ph·∫©m ƒë√£ h·∫øt h√†ng!");
      return;
    }

    const key = `cart_${user.email}`;
    let cart = JSON.parse(localStorage.getItem(key)) || [];

    const exist = cart.find((item) => item.id === product.id);

    if (exist) {
      exist.quantity += 1;
    } else {
      cart.push({ ...product, quantity: 1 });
    }

    localStorage.setItem(key, JSON.stringify(cart));
    window.dispatchEvent(new Event("storage"));
    alert("ƒê√£ th√™m v√†o gi·ªè!");
  };

  const buyNow = () => {
    addToCart();
    navigate("/cart");
  };

  const ratingStats = useMemo(() => {
    const total = reviews.length;
    if (total === 0) {
      return {
        avg: 0,
        total: 0,
        bars: [0, 0, 0, 0, 0],
      };
    }

    const bars = [0, 0, 0, 0, 0];
    let sum = 0;

    reviews.forEach((r) => {
      const rt = r.rating || 0;
      sum += rt;
      if (rt >= 1 && rt <= 5) {
        bars[rt - 1]++;
      }
    });

    return {
      avg: sum / total,
      total,
      bars,
    };
  }, [reviews]);

  const filteredReviews = useMemo(() => {
    let list = [...reviews];

    if (starFilter > 0) {
      list = list.filter((r) => r.rating === starFilter);
    }

    if (hasCommentOnly) {
      list = list.filter((r) => r.comment && r.comment.trim() !== "");
    }

    list.sort((a, b) => {
      switch (sortType) {
        case "newest":
          return new Date(b.createdAt) - new Date(a.createdAt);
        case "oldest":
          return new Date(a.createdAt) - new Date(b.createdAt);
        case "highest":
          return (b.rating || 0) - (a.rating || 0);
        case "lowest":
          return (a.rating || 0) - (b.rating || 0);
        default:
          return 0;
      }
    });

    return list;
  }, [reviews, starFilter, hasCommentOnly, sortType]);

  const renderStars = (rating) => {
    if (!rating || rating <= 0) return "Ch∆∞a c√≥ ƒë√°nh gi√°";

    const full = Math.round(rating);
    return "‚≠ê".repeat(full) + "‚òÜ".repeat(5 - full);
  };

  if (!product)
    return (
      <div className="loading">
        <div className="spinner-border text-danger"></div>
      </div>
    );

  return (
    <div className="pd-container">
      <ProfileHeader
        user={user}
        onLogout={() => {
          localStorage.removeItem("trip-token");
          setToken(null);
          setUser(null);
          window.location.href = "/";
        }}
      />

      <div className="pd-content">
        {/* LEFT */}
        <div className="pd-left">
          <div style={{ position: "relative" }}>
            {product.quantityInStock === 0 && (
              <div className="pd-out-stock">H·∫æT H√ÄNG</div>
            )}
            <img src={product.imageUrls?.[0]} alt={product.name} />
          </div>
        </div>

        {/* RIGHT */}
        <div className="pd-right">
          <h2 className="pd-name">{product.name}</h2>

          <div className="pd-rating-line">
            <span className="pd-rating-score">
              {ratingStats.avg ? ratingStats.avg.toFixed(1) : "0.0"}
            </span>

            <span className="pd-rating-stars">
              {renderStars(ratingStats.avg)}
            </span>

            <span className="pd-rating-count">
              ({ratingStats.total} ƒë√°nh gi√°)
            </span>
          </div>

          <span className="pd-price">
            {product.price.toLocaleString()} ‚Ç´
          </span>

          <div className="pd-actions">
            <button
              className="pd-buy"
              onClick={buyNow}
              disabled={product.quantityInStock === 0}
            >
              {product.quantityInStock === 0 ? "H·∫øt h√†ng" : "Mua ngay"}
            </button>

            <button
              className="pd-add"
              onClick={addToCart}
              disabled={product.quantityInStock === 0}
            >
              {product.quantityInStock === 0 ? "H·∫øt h√†ng" : "Th√™m v√†o gi·ªè"}
            </button>
          </div>

          <ul className="pd-spec">
            <li>‚ö° Chip: {product.chipset}</li>
            <li>üîã Pin: {product.battery}</li>
            <li>üíæ RAM: {product.ram}</li>
            <li>üíΩ B·ªô nh·ªõ: {product.storage}</li>
            <li>üé® M√†u: {product.color}</li>
          </ul>
        </div>
      </div>

      {/* M√î T·∫¢ */}
      <div className="pd-desc">
        <h3>M√¥ t·∫£ s·∫£n ph·∫©m</h3>
        <p>{product.description}</p>
      </div>

      {/* ============= ƒê√ÅNH GI√Å S·∫¢N PH·∫®M ============= */}
      <div className="pd-review-section">
        <h3>ƒê√°nh gi√° s·∫£n ph·∫©m</h3>

        {/* T·ªïng quan rating gi·ªëng Shopee */}
        <div className="pd-rating-box">
          <div className="pd-rating-left">
            <div className="pd-rating-number">
              {ratingStats.avg ? ratingStats.avg.toFixed(1) : "0.0"}
            </div>
            <div className="pd-stars-big">
              {renderStars(ratingStats.avg)}
            </div>
            <div className="pd-total">
              {ratingStats.total} ƒë√°nh gi√°
            </div>
          </div>

          <div className="pd-rating-right">
            {[5, 4, 3, 2, 1].map((s) => {
              const total = ratingStats.total || 1;
              const count = ratingStats.bars[s - 1] || 0;
              const percent = (count / total) * 100;
              return (
                <div key={s} className="pd-bar">
                  <span>{s} sao</span>
                  <div className="pd-bg">
                    <div
                      className="pd-fill"
                      style={{ width: `${percent}%` }}
                    ></div>
                  </div>
                  <span className="pd-bar-count">{count}</span>
                </div>
              );
            })}
          </div>
        </div>

   
        <div className="pd-filter-row">
          <div className="pd-filter-stars">
            <button
              className={
                "pd-filter-btn " + (starFilter === 0 ? "active" : "")
              }
              onClick={() => setStarFilter(0)}
            >
              T·∫•t c·∫£
            </button>
            {[5, 4, 3, 2, 1].map((s) => (
              <button
                key={s}
                className={
                  "pd-filter-btn " + (starFilter === s ? "active" : "")
                }
                onClick={() => setStarFilter(s)}
              >
                {s} sao
              </button>
            ))}

            <button
              className={
                "pd-filter-btn " + (hasCommentOnly ? "active" : "")
              }
              onClick={() => setHasCommentOnly((v) => !v)}
            >
              C√≥ b√¨nh lu·∫≠n
            </button>
          </div>

          <div className="pd-sort">
            <span>S·∫Øp x·∫øp: </span>
            <select
              value={sortType}
              onChange={(e) => setSortType(e.target.value)}
            >
              <option value="newest">M·ªõi nh·∫•t</option>
              <option value="oldest">C≈© nh·∫•t</option>
              <option value="highest">Sao cao ƒë·∫øn th·∫•p</option>
              <option value="lowest">Sao th·∫•p ƒë·∫øn cao</option>
            </select>
          </div>
        </div>

        {/* DANH S√ÅCH REVIEW */}
        <div className="pd-review-list">
          {filteredReviews.length === 0 && (
            <p className="pd-no-review">
              Ch∆∞a c√≥ ƒë√°nh gi√° ph√π h·ª£p v·ªõi b·ªô l·ªçc.
            </p>
          )}

          {filteredReviews.map((r) => (
            <div key={r.id} className="pd-review">
              <div className="pd-review-header">
                <div className="pd-review-avatar">
                  {r.userName?.charAt(0)?.toUpperCase() || "U"}
                </div>
                <div>
                  <div className="pd-review-name">{r.userName}</div>
                  <div className="pd-review-stars">
                    {"‚≠ê".repeat(r.rating || 0)}
                  </div>
                  {r.createdAt && (
                    <div className="pd-review-date">
                      {new Date(r.createdAt).toLocaleDateString("vi-VN")}
                    </div>
                  )}
                </div>
              </div>

              {r.comment && (
                <p className="pd-review-comment">{r.comment}</p>
              )}

              {r.adminReply && (
                <div className="pd-admin-reply">
                  <span className="pd-admin-label">Ph·∫£n h·ªìi c·ªßa shop:</span>
                  <p>{r.adminReply}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
