  useEffect(() => {
    if (!id1 || !id2) return;

    axios.get("http://localhost:8080/api/compare", {
      params: { p1: id1, p2: id2 }
    })
      .then((res) => {
        console.log("COMPARE:", res.data);
        setResult(res.data);
