import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

import {
  Package,
  ShoppingBag,
  Tag,
} from "lucide-react";


import AdminHeader from "./AdminHeader";  

export default function HomePageAdmin() {

  // dùng adminToken riêng!!
  const adminToken = localStorage.getItem("adminToken");

  const [products, setProducts] = useState([]);
  const [discounts, setDiscounts] = useState([]);

  useEffect(() => {
    const config = adminToken
      ? { headers: { Authorization: `Bearer ${adminToken}` } }
      : {};

    axios.get("http://localhost:8080/api/products", config)
      .then(res => {
        const data = Array.isArray(res.data) ? res.data : res.data.data;
        setProducts(data || []);
      });

    axios.get("http://localhost:8080/api/discounts", config)
      .then(res => {
        const data = Array.isArray(res.data) ? res.data : res.data.data;
        setDiscounts(data || []);
      });

  }, [adminToken]);

  return (
    <>
      <AdminHeader />
      <div style={{ height: 80 }}></div>

      <div style={{ padding: 20 }}>
        <h2>Trang quản trị</h2>
        
        <div style={{ display: "flex", gap: 20, marginTop: 20 }}>
          
          <Link to="/admin/products" style={box}>
            <Package size={26} />
            <p>Sản phẩm</p>
            <span>{products.length}</span>
          </Link>

          <Link to="/admin/products/add" style={box}>
            <Tag size={26} />
            <p>Thêm sản phẩm</p>
            <span>Tạo mới</span>
          </Link>

          <Link to="/admin/discounts" style={box}>
            <ShoppingBag size={26} />
            <p>Mã giảm giá</p>
            <span>{discounts.length}</span>
          </Link>

        </div>
      </div>
    </>
  );
}


const box = {
  width: 140,
  padding: 20,
  borderRadius: 10,
  background: "#fff",
  boxShadow: "0px 2px 10px rgba(0,0,0,0.08)",
  textAlign: "center",
  textDecoration: "none",
  color: "#333",
};
