import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import "../css/page/HomePage.css";
import ProfileHeader from "../components/page/PageHeader";
import { DataContext } from "../DataContext";
import { useNavigate } from "react-router-dom";

export default function HomePage() {

  const [products, setProducts] = useState([]);
  const [user, setUser] = useState(null);

  const [compareList, setCompareList] = useState([]);

  const { token, setToken } = useContext(DataContext);
  const navigate = useNavigate();


  /** LOAD COMPARE FROM LOCAL */
  useEffect(() => {
    const list = JSON.parse(localStorage.getItem("compare")) || [];
    setCompareList(list);
  }, []);


  /** ADD COMPARE */
  const addToCompare = (id) => {
    let list = [...compareList];

    if (!list.includes(id))
      list.push(id);

    if (list.length > 2)
      list.shift();

    localStorage.setItem("compare", JSON.stringify(list));
    setCompareList(list);
  };


  /** REMOVE COMPARE */
  const removeFromCompare = (id) => {
    const list = compareList.filter(x => x !== id);
    localStorage.setItem("compare", JSON.stringify(list));
    setCompareList(list);
  };


  function renderStars(rating) {
    if (!rating) return ;

    const full = Math.floor(rating);
    const half = rating - full >= 0.5;
    const empty = 5 - full - (half ? 1 : 0);

    return (
      <span className="hp-stars">
        {"‚≠ê".repeat(full)}
        {half && "‚≠ê"}
        {"‚òÜ".repeat(empty)}
        <span style={{ marginLeft: 4, fontSize: "13px", color: "#555" }}>
          {rating.toFixed(1)}
        </span>
      </span>
    );
  }


  /** GET USER */
  useEffect(() => {
    const localToken = localStorage.getItem("trip-token");

    if (!localToken) {
      setUser(null);
      return;
    }

    axios.get("http://localhost:8080/api/auth/user/me", {
      headers: { Authorization: `Bearer ${localToken}` },
    })
      .then(res => setUser(res.data.data))
      .catch(() => setUser(null));

  }, [token]);


  /** GET PRODUCTS */
  useEffect(() => {
    axios.get("http://localhost:8080/api/products")
      .then(res => {
        let list = res.data?.data || [];
        list = list.filter(p => p.status === "ACTIVE");
        setProducts(list);
      })
      .catch(err => console.log(err));
  }, []);


  /** LOGOUT */
  const handleLogout = () => {
    localStorage.removeItem("trip-token");
    setToken(null);
    setUser(null);
    navigate("/");
  };


  /** ADD CART */
  const addToCart = (product) => {
    const localToken = localStorage.getItem("trip-token");

    if (!localToken || !user) {
      alert("Vui l√≤ng ƒëƒÉng nh·∫≠p tr∆∞·ªõc!");
      navigate("/login");
      return;
    }

    const key = `cart_${user.email}`;

    let cart = JSON.parse(localStorage.getItem(key)) || [];

    const found = cart.find((item) => item.id === product.id);

    if (found) {
    found.quantity += 1;
  } else {
    cart.push({
      id: product.id,
      name: product.name,
      price: product.price,
      quantity: 1,
      imageUrls: product.imageUrls 
    });
  }
    localStorage.setItem(key, JSON.stringify(cart));

    window.dispatchEvent(new Event("storage"));

    alert("ƒê√£ th√™m v√†o gi·ªè h√†ng!");
  };



  return (
    <div className="hp-container">

      <ProfileHeader user={user} onLogout={handleLogout} />


      {/* ----------- Banner ----------- */}
      <section className="hp-banner">
        <div className="hp-banner-slide">üî• Khuy·∫øn m√£i c·ª±c s·ªëc!</div>
      </section>


      {/* ----------- Category ----------- */}
      <section className="hp-category">
        <div className="hp-cat-item">üì± ƒêi·ªán tho·∫°i</div>
        <div className="hp-cat-item">Tin t·ª©c</div>
      </section>


      {/* ----------- COMPARE BOX ----------- */}
      {compareList.length > 0 && (
        <div className="compare-box">
          <h4>So s√°nh s·∫£n ph·∫©m:</h4>

          <div className="compare-items">
            {compareList.map(id => {
              const p = products.find(x => x.id === id);
              if (!p) return null;

              return (
                <div className="compare-item" key={id}>
  
  <img
    src={p.imageUrls?.[0]}
    alt={p.name}
    className="compare-thumb"
  />

  <span>{p.name}</span>

  <button onClick={() => removeFromCompare(id)}>√ó</button>
</div>

              );
            })}
          </div>

          {compareList.length === 2 && (
            <button
              className="compare-btn"
              onClick={() =>
                navigate(`/compare?p1=${compareList[0]}&p2=${compareList[1]}`)}
            >
              Xem so s√°nh ‚Üí
            </button>
          )}
        </div>
      )}



      {/* ----------- FLASH SALE ----------- */}
      <section className="hp-section">
        <h2>üî• FLASH SALE</h2>

        <div className="hp-scroll-row">
          {products.map((p) => (
            <div key={p.id} className="hp-flash-card">

              <div style={{ position: "relative" }}>
                {p.quantityInStock === 0 && (
                  <div className="hp-out-stock">H·∫æT H√ÄNG</div>
                )}
                <img
                  src={p.imageUrls?.[0]}
                  alt={p.name}
                  onClick={() => navigate(`/product/${p.id}`)}
                />
              </div>

              <div className="hp-name">{p.name}</div>
              <div>{renderStars(p.averageRating)}</div>

              <div className="hp-price">{p.price.toLocaleString()} ‚Ç´</div>

              <button className="btn-compare" onClick={() => addToCompare(p.id)}>
                + So s√°nh
              </button>

              <button
                className="btn-add-cart"
                disabled={p.quantityInStock === 0}
                onClick={() => addToCart(p)}
              >
                {p.quantityInStock === 0 ? "H·∫øt h√†ng" : "üõí Th√™m v√†o gi·ªè"}
              </button>

            </div>
          ))}
        </div>
      </section>



      {/* ----------- G·ª£i √Ω ----------- */}
      <section className="hp-section">
        <h2>G·ª£i √Ω h√¥m nay</h2>

        <div className="hp-grid">
          {products.map((p) => (
            <div key={p.id} className="hp-card">

              <div style={{ position: "relative" }}>
                {p.quantityInStock === 0 && (
                  <div className="hp-out-stock">H·∫æT H√ÄNG</div>
                )}
                <img
                  src={p.imageUrls?.[0]}
                  alt={p.name}
                  onClick={() => navigate(`/product/${p.id}`)}
                />
              </div>

              <div className="hp-info">
                <span className="hp-brand">{p.brand}</span>

                <h3
                  onClick={() => navigate(`/product/${p.id}`)}
                  style={{ cursor: "pointer" }}
                >
                  {p.name}
                </h3>

                <div>{renderStars(p.averageRating)}</div>

                <p>{p.price.toLocaleString()} ‚Ç´</p>

                <button className="btn-compare" onClick={() => addToCompare(p.id)}>
                  + So s√°nh
                </button>

                <button
                  className="btn-add-cart"
                  disabled={p.quantityInStock === 0}
                  onClick={() => addToCart(p)}
                >
                  {p.quantityInStock === 0 ? "H·∫øt h√†ng" : "üõí Th√™m v√†o gi·ªè"}
                </button>

              </div>
            </div>
          ))}
        </div>
      </section>

    </div>
  );
}
