import { createContext, useState, useEffect, useCallback } from "react";
import axios from "axios";

export const DataContext = createContext();

export const DataProvider = ({ children }) => {
  const [token, setToken] = useState(localStorage.getItem("trip-token"));
  const [user, setUser] = useState(null);
  const [loadingUser, setLoadingUser] = useState(true);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    if (token) {
      localStorage.setItem("trip-token", token);
    } else {
      localStorage.removeItem("trip-token");
      setUser(null);
      setCart([]);
    }
  }, [token]);

const fetchCart = useCallback(async () => {
  if (!token) return;
  try {
    const res = await axios.get("http://localhost:8080/api/cart", {
      headers: { Authorization: `Bearer ${token}` },
    });
    // Kiểm tra đa tầng: ưu tiên res.data.data, sau đó tới res.data
    const actualData = res.data.data || res.data;
    setCart(Array.isArray(actualData) ? actualData : []);
  } catch (err) {
    console.error("Fetch cart failed:", err);
    setCart([]);
  }
}, [token]);

  useEffect(() => {
    const fetchUser = async () => {
      if (!token) {
        setLoadingUser(false);
        return;
      }
      try {
        const res = await axios.get("http://localhost:8080/api/auth/user/me", {
          headers: { Authorization: `Bearer ${token}` },
        });
        const userData = res.data.data || res.data;
        setUser(userData);
      } catch (err) {
        setToken(null);
      } finally {
        setLoadingUser(false);
      }
    };
    fetchUser();
  }, [token]);

  // Tự động fetch giỏ hàng khi user vừa đăng nhập xong
  useEffect(() => {
    if (user) fetchCart();
  }, [user, fetchCart]);

  const logout = () => {
    setToken(null);
    setUser(null);
    setCart([]);
    localStorage.removeItem("trip-token");
  };

  return (
    <DataContext.Provider value={{ token, setToken, user, setUser, logout, loadingUser, cart, setCart, fetchCart }}>
      {children}
    </DataContext.Provider>
  );
};
