import { Navigate, Route, Routes } from "react-router-dom";
import "./App.css";
import { privateRoutes, publicRoutes, privateAdminRoutes } from "./configs/routeConfig";
import { useContext } from "react";
import { DataContext } from "./DataContext";
import HomePage from "./pages/HomePage";

// ðŸ‘‡ import ToastContainer
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function App() {
  const { token } = useContext(DataContext);
  const adminToken = localStorage.getItem("adminToken");

  return (
    <>
      <Routes>
        <Route path="/" element={<HomePage />} />

        {publicRoutes.map((route, index) => (
          <Route key={index} path={route.path} element={route.element} />
        ))}

        {privateRoutes.map((route, index) => (
          <Route
            key={index}
            path={route.path}
            element={token ? route.element : <Navigate to="/login" replace />}
          />
        ))}

        {/* â­ ADMIN */}
        {privateAdminRoutes.map((route, index) => (
          <Route
            key={index}
            path={route.path}
            element={adminToken ? route.element : <Navigate to="/admin/login" replace />}
          />
        ))}
      </Routes>

      {/* ðŸ‘‡ Pháº£i Ä‘á»ƒ á»Ÿ ngoÃ i Routes */}
      <ToastContainer />
    </>
  );
}

export default App;
