import React, { useEffect, useState, useContext } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import "../css/page/ProductDetail.css";
import ProfileHeader from "../components/page/PageHeader";
import { DataContext } from "../DataContext";

export default function ProductDetail() {

  const { id } = useParams();
  const navigate = useNavigate();

  const { token,setToken } = useContext(DataContext);

  const [product, setProduct] = useState(null);
  const [user,setUser] = useState(null);

  // load product
  useEffect(() => {
    axios.get(`http://localhost:8080/api/products/${id}`)
      .then(res => setProduct(res.data.data))
      .catch(err => console.log(err));
  }, [id]);


  // load user n·∫øu ƒë√£ login
  useEffect(()=>{
    if(!token){
      setUser(null);
      return;
    }

    axios.get("http://localhost:8080/api/auth/user/me",{
      headers:{ Authorization:`Bearer ${token}` }
    })
    .then(res=>setUser(res.data.data))
    .catch(()=>setUser(null))

  },[token]);


  // üìå ADD TO CART (THEO USER)
  const addToCart = () => {
    const localToken = localStorage.getItem("trip-token");

    if (!localToken || !user) {
      alert("B·∫°n c·∫ßn ƒëƒÉng nh·∫≠p ƒë·ªÉ th√™m v√†o gi·ªè h√†ng!");
      navigate("/login");
      return;
    }

    // m·ªói user 1 cart
    const key = `cart_${user.email}`;

    let cart = JSON.parse(localStorage.getItem(key)) || [];

    const exist = cart.find(item => item.id === product.id);

    if(exist){
      exist.quantity += 1;
    }else{
      cart.push({ ...product, quantity: 1 });
    }

    localStorage.setItem(key, JSON.stringify(cart));

    // ‚≠ê‚≠ê D√íNG N√ÄY L√Ä FIX ‚≠ê‚≠ê
    window.dispatchEvent(new Event("storage"));

    alert("ƒê√£ th√™m v√†o gi·ªè!");
  };


  const buyNow = () => {
    addToCart();
    navigate("/cart");
  }


  if (!product)
    return <div className="loading"><div className="spinner-border text-danger"></div></div>


  return (
    <div className="pd-container">

      <ProfileHeader user={user} onLogout={()=>{
        localStorage.removeItem("trip-token");
        setToken(null);
        setUser(null);
        window.location.href="/";
      }}/>

      <div className="pd-content">

        <div className="pd-left">
          <img src={product.images[0]?.imageUrl} alt={product.name}/>
        </div>

        <div className="pd-right">

          <h2>{product.name}</h2>
          <span className="pd-price">{product.price.toLocaleString()} ‚Ç´</span>

          <button className="pd-buy" onClick={buyNow}>
            Mua ngay
          </button>

          <button className="pd-add" onClick={addToCart}>
            Th√™m v√†o gi·ªè
          </button>

          <ul className="pd-spec">
            <li>‚ö° Chip: {product.chipset}</li>
            <li>üîã Pin: {product.battery}</li>
          </ul>

        </div>

      </div>

      <div className="pd-desc">
        <h3>M√¥ t·∫£</h3>
        <p>{product.description}</p>
      </div>
    </div>
  );
}
