import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import ".."

import {
  Package,
  ShoppingBag,
  Tag,
  BarChart2,
  ChevronRight,
} from "lucide-react";

import AdminHeader from "./AdminHeader";

export default function HomeAdmin() {

  const navigate = useNavigate();
  const adminToken = localStorage.getItem("adminToken");

  const [products, setProducts] = useState([]);

  // ⭐ NEW: tách state orderStats
  const [orderStats, setOrderStats] = useState({
    pending: 0,
    confirmed: 0,
    shipping: 0,
    completed: 0,
    cancelled: 0,
  });

  useEffect(() => {

    if (!adminToken) {
      navigate("/admin/login");
      return;
    }

    const config = {
      headers: { Authorization: "Bearer " + adminToken }
    };

    // load product
    axios.get("http://localhost:8080/api/products", config)
      .then((res) => {
        const data = Array.isArray(res.data) ? res.data : res.data.data;
        setProducts(data || []);
      })
      .catch((err) => {
        if (err.response?.status === 401) {
          localStorage.removeItem("adminToken");
          navigate("/admin/login");
        }
      });

    // ⭐ load orders
    axios.get("http://localhost:8080/api/orders", config)
      .then((res) => {
        const list = res.data || [];

        setOrderStats({
          pending: list.filter(o => o.status === "PENDING").length,
          confirmed: list.filter(o => o.status === "CONFIRMED").length,
          shipping: list.filter(o => o.status === "SHIPPING").length,
          completed: list.filter(o => o.status === "COMPLETED").length,
          cancelled: list.filter(o => o.status === "CANCELLED").length,
        });
      });

  }, []);


  return (
    <>
      <AdminHeader />
      <div style={{ height: 80 }}></div>

      <div style={styles.wrapper}>
        <div style={styles.container}>

          {/* ------------ QUẢN LÝ SẢN PHẨM ------------ */}
          <div style={styles.card}>
            <div style={styles.cardHeader}>
              <h2 style={styles.cardTitle}>Quản Lý Sản Phẩm</h2>
              <Link to="/admin/products" style={styles.more}>Xem thêm</Link>
            </div>

            <div style={styles.row4}>

              <Link to="/admin/products" style={styles.itemBox}>
                <Package size={26} color="#ee4d2d" />
                <p>Số sản phẩm</p>
                <span>{products.length}</span>
              </Link>

              <Link to="/admin/products/add" style={styles.itemBox}>
                <Tag size={26} color="#3b82f6" />
                <p>Thêm sản phẩm</p>
                <span>Tạo mới</span>
              </Link>

              <Link to="/admin/reviews" style={styles.itemBox}>
                <Tag size={26} color="#3b82f6" />
                <p>Đánh giá</p>
              </Link>

            </div>
          </div>


          {/* ------------ QUẢN LÝ ĐƠN HÀNG ------------ */}
          <div style={styles.card}>
            <div style={styles.cardHeader}>
              <h2 style={styles.cardTitle}>Quản Lý Đơn Hàng</h2>
              <Link to="/admin/orders" style={styles.more}>Xem thêm</Link>
            </div>

            <div style={styles.row4}>

              <Link to="/admin/orders?status=PENDING" style={styles.itemBox}>
                <ShoppingBag size={26} color="#ef4444" />
                <p>Chờ xử lý</p>
                <span>{orderStats.pending}</span>
              </Link>

              <Link to="/admin/orders?status=SHIPPING" style={styles.itemBox}>
                <ShoppingBag size={26} color="#3b82f6" />
                <p>Đang giao</p>
                <span>{orderStats.shipping}</span>
              </Link>

              <Link to="/admin/orders?status=COMPLETED" style={styles.itemBox}>
                <ShoppingBag size={26} color="#22c55e" />
                <p>Hoàn thành</p>
                <span>{orderStats.completed}</span>
              </Link>

              <Link to="/admin/orders?status=CANCELLED" style={styles.itemBox}>
                <ShoppingBag size={26} color="#6b7280" />
                <p>Đã hủy</p>
                <span>{orderStats.cancelled}</span>
              </Link>

            </div>
          </div>


          {/* ------------ DOANH THU ------------ */}
          <div style={styles.card}>
            <div style={styles.cardHeader}>
              <h2 style={styles.cardTitle}>Thống Kê Doanh Thu</h2>
              <Link to="/admin/new/new" style={styles.more}>Xem thêm</Link>
            </div>

            <div style={styles.row3}>
              <div style={styles.statBox}>
                <BarChart2 size={28} color="#ef4b2d" />
                <p>Doanh thu hôm nay</p>
                <h3>₫1,250,000</h3>
              </div>

              <div style={styles.statBox}>
                <BarChart2 size={28} color="#16a34a" />
                <p>Doanh thu tháng</p>
                <h3>₫32,550,000</h3>
              </div>

              <div style={styles.statBox}>
                <BarChart2 size={28} color="#3b82f6" />
                <p>Tổng lượt mua</p>
                <h3>580</h3>
              </div>
            </div>
          </div>


        </div>
      </div>
    </>
  );
}



/* =============== STYLE =============== */
const styles = {
  wrapper: {
    padding: "20px 0",
    background: "#f5f5f5",
    minHeight: "100vh",
  },
  container: {
    maxWidth: 1380,
    margin: "0 auto",
    display: "flex",
    flexDirection: "column",
    gap: 20,
  },
  card: {
    background: "#fff",
    padding: "2px 25px",
    borderRadius: 12,
    border: "1px solid #eee",
  },
  cardHeader: {
    display: "flex",
    justifyContent: "space-between",
    marginBottom: 15,
  },
  cardTitle: {
    fontSize: 18,
    fontWeight: 600,
    color: "#333",
  },
  more: {
    color: "#007bff",
    fontSize: 14,
    textDecoration: "none",
  },
  row4: {
    display: "grid",
    gridTemplateColumns: "repeat(4, 1fr)",
    gap: 20,
  },
  row3: {
    display: "grid",
    gridTemplateColumns: "repeat(3, 1fr)",
    gap: 20,
  },
  itemBox: {
    border: "1px solid #eee",
    padding: "18px 20px",
    borderRadius: 10,
    background: "#fafafa",
    textDecoration: "none",
    color: "#3f1616ff",
    display: "flex",
    flexDirection: "column",
    gap: 8,
    fontSize: 14,
  },
  statBox: {
    border: "1px solid #eee",
    padding: "20px",
    borderRadius: 10,
    background: "#fafafa",
    textAlign: "center",
  },
};
