import React, { useEffect, useState } from "react";
import axios from "axios";
import AdminHeader from "../page/AdminHeader";

export default function ReviewManagement() {
  const token = localStorage.getItem("adminToken");

  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);

  const [filterStar, setFilterStar] = useState(0);
  const [sort, setSort] = useState("newest");

  const [replyText, setReplyText] = useState("");
  const [selectedReview, setSelectedReview] = useState(null);

  // load all reviews
  useEffect(() => {
    axios.get("http://localhost:8080/api/reviews", {
      headers: { Authorization: "Bearer " + token }
    })
      .then(res => setReviews(res.data.data || []))
      .finally(() => setLoading(false))
      .catch(err => console.log(err));
  }, []);

  const deleteReview = async (reviewId) => {
    if (!window.confirm("Xóa đánh giá này?")) return;

    try {
      await axios.delete(
        `http://localhost:8080/api/reviews/${reviewId}`,
        { headers: { Authorization: "Bearer " + token } }
      );

      setReviews(reviews.filter(r => r.id !== reviewId));
    } catch (err) {
      console.log(err);
    }
  };

  const sendReply = async () => {
    try {
      await axios.put(
        `http://localhost:8080/api/reviews/${selectedReview.id}/reply`,
        { adminReply: replyText },
        { headers: { Authorization: "Bearer " + token } }
      );

      // update UI
      const next = reviews.map(r =>
        r.id === selectedReview.id ? { ...r, adminReply: replyText } : r
      );
      setReviews(next);

      setReplyText("");
      setSelectedReview(null);

      alert("Đã gửi phản hồi");
    } catch (err) {
      console.log(err);
    }
  };

  const filtered = reviews
    .filter(r => filterStar === 0 || r.rating === filterStar)
    .sort((a, b) => {
      switch (sort) {
        case "newest":
          return new Date(b.createdAt) - new Date(a.createdAt);
        case "oldest":
          return new Date(a.createdAt) - new Date(b.createdAt);
        case "highest":
          return b.rating - a.rating;
        case "lowest":
          return a.rating - b.rating;
        default:
          return 0;
      }
    });

  return (
    <>
      <AdminHeader />

      <div style={{ padding: 30 }}>

        <h2 style={{ marginBottom: 20 }}>
          Quản lý đánh giá sản phẩm
        </h2>

        {/* FILTER */}
        <div style={{ marginBottom: 15, display: "flex", gap: 15 }}>
          <select value={filterStar} onChange={e => setFilterStar(Number(e.target.value))}>
            <option value={0}>Tất cả</option>
            {[5,4,3,2,1].map(s => (
              <option key={s} value={s}>{s} sao</option>
            ))}
          </select>

          <select value={sort} onChange={e => setSort(e.target.value)}>
            <option value="newest">Mới nhất</option>
            <option value="oldest">Cũ nhất</option>
            <option value="highest">Sao cao đến thấp</option>
            <option value="lowest">Sao thấp đến cao</option>
          </select>
        </div>

        {loading && <p>Đang tải...</p>}

        {filtered.map(r => (
          <div key={r.id} style={styles.card}>
            <div style={{ display:"flex", justifyContent:"space-between" }}>
              <div>
                <strong>{r.userName}</strong> ⭐{r.rating}  
                <div style={{ color:"#777" }}>
                  {new Date(r.createdAt).toLocaleDateString("vi-VN")}
                </div>
              </div>

              <button
                style={styles.deleteBtn}
                onClick={() => deleteReview(r.id)}
              >
                Xóa
              </button>
            </div>

            {r.comment && (
              <div style={{marginTop:8}}>
                "{r.comment}"
              </div>
            )}

            {/* ADMIN REPLY */}
            {r.adminReply ? (
              <div style={styles.replyBox}>
                <b>Phản hồi:</b> {r.adminReply}
              </div>
            ) : (
              <button
                style={styles.replyBtn}
                onClick={() => setSelectedReview(r)}
              >
                Trả lời
              </button>
            )}
          </div>
        ))}

        {/* POPUP */}
        {selectedReview && (
          <div style={styles.popup}>
            <div style={styles.popupBox}>
              <h3>Phản hồi đánh giá</h3>
              <textarea
                style={styles.textarea}
                value={replyText}
                onChange={e => setReplyText(e.target.value)}
              />
              <button style={styles.saveBtn} onClick={sendReply}>
                Gửi phản hồi
              </button>
              <button style={styles.cancelBtn} onClick={()=>setSelectedReview(null)}>
                Hủy
              </button>
            </div>
          </div>
        )}

      </div>
    </>
  );
}

const styles = {
  card: {
    background:"#fff",
    padding:15,
    borderRadius:8,
    marginBottom:12,
    border:"1px solid #ddd"
  },
  replyBtn: {
    marginTop:10,
    background:"#007bff",
    color:"#fff",
    padding:"6px 10px",
    borderRadius:6,
    border:"none",
    cursor:"pointer"
  },
  deleteBtn: {
    background:"#dc3545",
    color:"#fff",
    padding:"5px 10px",
    borderRadius:6,
    border:"none",
    cursor:"pointer"
  },
  replyBox:{
    padding:10,
    background:"#fafafa",
    borderRadius:6,
    marginTop:8
  },
  popup:{
    position:"fixed",
    left:0, top:0, right:0, bottom:0,
    background:"rgba(0,0,0,0.5)",
    display:"flex",
    alignItems:"center",
    justifyContent:"center"
  },
  popupBox:{
    background:"#fff",
    padding:20,
    borderRadius:8,
    width:400
  },
  textarea:{
    width:"100%",
    height:100,
    margin:"8px 0"
  },
  saveBtn:{
    background:"#28a745",
    color:"#fff",
    padding:"8px 12px",
    borderRadius:6,
    border:"none",
    cursor:"pointer",
    marginRight:10
  },
  cancelBtn:{
    padding:"8px 12px",
    borderRadius:6,
    border:"1px solid #aaa",
    cursor:"pointer"
  }
};
