import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios"; 

import {
  Package,
  ShoppingBag,
  Tag,
  BarChart2,
  ChevronRight,
} from "lucide-react";

import AdminHeader from "./AdminHeader";

export default function HomeAdmin() {

  const adminToken = localStorage.getItem("adminToken");

  const [products, setProducts] = useState([]);
  const [discounts, setDiscounts] = useState([]);

 useEffect(() => {
  const config = adminToken
    ? { headers: { Authorization: `Bearer ${adminToken}` } }
    : {};

  axios.get("http://localhost:8080/api/admin/products", config)
    .then(res => {
      const data = Array.isArray(res.data) ? res.data : res.data.data;
      setProducts(data || []);
    })
    .catch(err => {
      console.log("Product error", err);
      if (err.response?.status === 401) {
        localStorage.removeItem("adminToken");
        navigate("/admin/login");
      }
    });

}, []);
// üëà FIXED HERE

  return (
    <>
      <AdminHeader />
      <div style={{ height: 80 }}></div>

      <div style={styles.wrapper}>
        <div style={styles.container}>

          {/* QU·∫¢N L√ù S·∫¢N PH·∫®M */}
          <div style={styles.card}>
            <div style={styles.cardHeader}>
              <h2 style={styles.cardTitle}>Qu·∫£n L√Ω S·∫£n Ph·∫©m</h2>
              <Link to="/admin/products" style={styles.more}>Xem th√™m</Link>
            </div>

            <div style={styles.row4}>

              <Link to="/admin/products" style={styles.itemBox}>
                <Package size={26} color="#ee4d2d" />
                <p>S·ªë s·∫£n ph·∫©m</p>
                <span>{products.length}</span>
              </Link>

              <Link to="/admin/products/add" style={styles.itemBox}>
                <Tag size={26} color="#3b82f6" />
                <p>Th√™m s·∫£n ph·∫©m</p>
                <span>T·∫°o m·ªõi</span>
              </Link>

              <Link to="/admin/discounts" style={styles.itemBox}>
                <ShoppingBag size={26} color="#a855f7" />
                <p>M√£ gi·∫£m gi√°</p>
                <span>{discounts.length}</span>
              </Link>

            </div>
          </div>

          {/* ƒê∆†N H√ÄNG */}
          <div style={styles.card}>
            <div style={styles.cardHeader}>
              <h2 style={styles.cardTitle}>Qu·∫£n L√Ω ƒê∆°n H√†ng</h2>
              <Link to="/admin/orders" style={styles.more}>Xem th√™m</Link>
            </div>

            <div style={styles.row4}>

              <Link to="/admin/orders?status=processing" style={styles.itemBox}>
                <ShoppingBag size={26} color="#ef4444" />
                <p>Ch·ªù x·ª≠ l√Ω</p>
                <span>5</span>
              </Link>

              <Link to="/admin/orders?status=shipping" style={styles.itemBox}>
                <ShoppingBag size={26} color="#3b82f6" />
                <p>ƒêang giao</p>
                <span>12</span>
              </Link>

              <Link to="/admin/orders?status=completed" style={styles.itemBox}>
                <ShoppingBag size={26} color="#22c55e" />
                <p>Ho√†n th√†nh</p>
                <span>31</span>
              </Link>

              <Link to="/admin/orders?status=cancelled" style={styles.itemBox}>
                <ShoppingBag size={26} color="#6b7280" />
                <p>ƒê√£ h·ªßy</p>
                <span>3</span>
              </Link>

            </div>
          </div>

          {/* Th·ªëng k√™ */}
          <div style={styles.card}>
            <div style={styles.cardHeader}>
              <h2 style={styles.cardTitle}>Th·ªëng K√™ Doanh Thu</h2>
              <Link to="/admin/revenue" style={styles.more}>Xem th√™m</Link>
            </div>

            <div style={styles.row3}>
              <div style={styles.statBox}>
                <BarChart2 size={28} color="#ef4b2d" />
                <p>Doanh thu h√¥m nay</p>
                <h3>‚Ç´1,250,000</h3>
              </div>

              <div style={styles.statBox}>
                <BarChart2 size={28} color="#16a34a" />
                <p>Doanh thu th√°ng</p>
                <h3>‚Ç´32,550,000</h3>
              </div>

              <div style={styles.statBox}>
                <BarChart2 size={28} color="#3b82f6" />
                <p>T·ªïng l∆∞·ª£t mua</p>
                <h3>580</h3>
              </div>
            </div>
          </div>

        </div>
      </div>
    </>
  );
}


/* =============== CSS STYLE =============== */

const styles = {
  wrapper: {
    padding: "20px 0",
    background: "#f5f5f5",
    minHeight: "100vh",
  },
  container: {
    maxWidth: 1380,
    margin: "0 auto",
    display: "flex",
    flexDirection: "column",
    gap: 20,
  },
  card: {
    background: "#fff",
    padding: "20px 25px",
    borderRadius: 12,
    border: "1px solid #eee",
  },
  cardHeader: {
    display: "flex",
    justifyContent: "space-between",
    marginBottom: 15,
  },
  cardTitle: {
    fontSize: 18,
    fontWeight: 600,
    color: "#333",
  },
  more: {
    color: "#007bff",
    fontSize: 14,
    textDecoration: "none",
  },
  row4: {
    display: "grid",
    gridTemplateColumns: "repeat(4, 1fr)",
    gap: 20,
  },
  row3: {
    display: "grid",
    gridTemplateColumns: "repeat(3, 1fr)",
    gap: 20,
  },
  itemBox: {
    border: "1px solid #eee",
    padding: "18px 20px",
    borderRadius: 10,
    background: "#fafafa",
    cursor: "pointer",
    textDecoration: "none",
    color: "#333",
    display: "flex",
    flexDirection: "column",
    gap: 8,
    fontSize: 14,
  },
  statBox: {
    border: "1px solid #eee",
    padding: "20px",
    borderRadius: 10,
    background: "#fafafa",
    textAlign: "center",
  },
};
