import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";   // üëà NEW
import axios from "axios";
import "../../css/admin/page/AdminOrdersPage.css";

import AdminHeader from "./AdminHeader";

export default function AdminOrdersPage() {
  const [orders, setOrders] = useState([]);

  const location = useLocation();   // üëà NEW
  const query = new URLSearchParams(location.search);
  const selectedStatus = query.get("status"); // üëà l·∫•y status t·ª´ URL

  useEffect(() => {
    loadOrders();
  }, []);

  const loadOrders = async () => {
    const token = localStorage.getItem("adminToken");

    try {
      const res = await axios.get("http://localhost:8080/api/orders", {
        headers: { Authorization: `Bearer ${token}` },
      });

      // ‚≠ê S·∫ÆP X·∫æP M·ªöI NH·∫§T TR∆Ø·ªöC
      const sorted = (res.data || []).sort(
        (a, b) => new Date(b.createdAt) - new Date(a.createdAt)
      );

      setOrders(sorted);

    } catch (err) {
      console.log("ERROR =====> ", err.response?.data);
    }
  };

  const changeStatus = async (id, status) => {
    const token = localStorage.getItem("adminToken");

    try {
      await axios.put(
        `http://localhost:8080/api/orders/${id}/status`,
        { status },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      loadOrders();
    } catch (err) {
      alert(err.response?.data || "Kh√¥ng th·ªÉ c·∫≠p nh·∫≠t tr·∫°ng th√°i");
    }
  };

  const renderStatus = (s) => {
    const icons = {
      PENDING: "‚è≥",
      CONFIRMED: "‚úîÔ∏è",
      SHIPPING: "üöö",
      COMPLETED: "üéâ",
      CANCELLED: "‚ùå",
    };
    const labels = {
      PENDING: "Ch·ªù x√°c nh·∫≠n",
      CONFIRMED: "ƒê√£ x√°c nh·∫≠n",
      SHIPPING: "ƒêang giao",
      COMPLETED: "Ho√†n th√†nh",
      CANCELLED: "ƒê√£ h·ªßy",
    };
    return `${icons[s]} ${labels[s]}`;
  };

  const isLocked = (s) =>
    s === "COMPLETED" || s === "CANCELLED";

  // ‚≠ê FILTER THEO STATUS TRONG URL
  const filteredOrders = selectedStatus
    ? orders.filter((o) => o.status === selectedStatus)
    : orders;

  return (
    <>
      <AdminHeader />

      <div className="admin-orders">
        <h2>
          Qu·∫£n l√Ω ƒë∆°n h√†ng{" "}
          {selectedStatus && <small style={{color:"#666"}}>(l·ªçc: {renderStatus(selectedStatus)})</small>}
        </h2>

        <table>
          <thead>
            <tr>
              <th>#</th>
              <th>Kh√°ch h√†ng</th>
              <th>Li√™n h·ªá</th>
              <th>ƒê·ªãa ch·ªâ</th>
              <th>T·ªïng ti·ªÅn</th>
              <th>Tr·∫°ng th√°i</th>
              <th>C·∫≠p nh·∫≠t</th>
            </tr>
          </thead>

          <tbody>
            {filteredOrders.map((o) => (
              <tr key={o.id}>
                <td>#{o.id}</td>

                <td><strong>{o.fullName}</strong></td>

                <td>{o.phone}</td>

                <td style={{ maxWidth: 250 }}>{o.address}</td>

                <td style={{ color: "#f63", fontWeight: 600 }}>
                  {o.totalPrice.toLocaleString()} ‚Ç´
                </td>

                <td>
                  <span className={`st-${o.status}`}>{renderStatus(o.status)}</span>
                </td>

                <td>
                  <select
                    disabled={isLocked(o.status)}
                    value={o.status}
                    onChange={(e) => changeStatus(o.id, e.target.value)}
                    style={{
                      cursor: isLocked(o.status) ? "not-allowed" : "pointer",
                      background: isLocked(o.status) ? "#eee" : "#fff",
                    }}
                  >
                    <option value="PENDING">Ch·ªù x√°c nh·∫≠n</option>
                    <option value="CONFIRMED">ƒê√£ x√°c nh·∫≠n</option>
                    <option value="SHIPPING">ƒêang giao</option>
                    <option value="COMPLETED">Ho√†n th√†nh</option>
                    <option value="CANCELLED">ƒê√£ h·ªßy</option>
                  </select>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
}
