  const id2 = params.get("p2");

  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!id1 || !id2) return;

    axios
      .get("http://localhost:8080/api/compare", { params: { p1: id1, p2: id2 } })
