import React, { useContext, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { Link, useNavigate } from "react-router-dom";
import { DataContext } from "../DataContext";
import ProfileHeader from "../components/page/PageHeader";

function LoginPage() {
  const navigate = useNavigate();
  const [data, setData] = useState({ email: "", password: "" });
  const [loading, setLoading] = useState(false);
  const { setToken } = useContext(DataContext);

  const handleChangeInput = (e) => {
    const { name, value } = e.target;
    setData({ ...data, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // -- LOGIN
      const res = await axios.post("http://localhost:8080/api/auth/login", data);
      const token = res.data.accessToken || res.data.access_token;

      if (!token) {
        toast.error("Token không hợp lệ từ server");
        return;
      }

      localStorage.setItem("trip-token", token);
      setToken(token);

      // -- GET USER REAL TIME FROM DATABASE
      const userRes = await axios.get("http://localhost:8080/api/auth/user/me", {
        headers: { Authorization: `Bearer ${token}` }
      });

      const user = userRes.data.data;
      localStorage.setItem("user-data", JSON.stringify(user));

      toast.success("Đăng nhập thành công!");
      navigate("/");

    } catch (err) {
      toast.error("Sai email hoặc mật khẩu");
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <ProfileHeader />

      <div className="bg-light min-vh-100 d-flex justify-content-center align-items-center"
           style={{ background: "#fffaf5" }}>

        <div className="col-md-5 col-lg-4">
          <div className="card border-0 shadow-lg rounded-4">
            <div className="card-body p-5">

              <h2 className="text-center fw-bold mb-2" style={{ color: "#ff7a29" }}>
                Welcome Back
              </h2>

              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <input type="email" name="email"
                         onChange={handleChangeInput}
                         placeholder="Email" required />
                </div>

                <div className="mb-3">
                  <input type="password" name="password"
                         onChange={handleChangeInput}
                         placeholder="Password" required />
                </div>

                <button type="submit" className="btn btn-primary" disabled={loading}>
                  {loading ? "Loading..." : "Login"}
                </button>
              </form>

            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default LoginPage;
