import React, { useContext, useState } from "react";
import axios from "axios";
import { toast, ToastContainer } from "react-toastify";
import { useNavigate } from "react-router-dom";

import PageHeader from "../components/page/PageHeader";
import { DataContext } from "../DataContext";
import "../css/page/ChangePasswordPage.css";

function ChangePasswordPage() {
  const { token } = useContext(DataContext);
  const navigate = useNavigate();

  const [form, setForm] = useState({
    email: "",
    oldPassword: "",
    newPassword: "",
    confirmPassword: "",
  });

  const [loading, setLoading] = useState(false);

  const handleChangeInput = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (form.newPassword !== form.confirmPassword) {
      toast.error("Mật khẩu xác nhận không khớp!");
      return;
    }

    setLoading(true);
    try {
      const res = await axios.post(
        "http://localhost:8080/api/auth/change-password",
        form,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (res.status === 200) {
        toast.success("Đổi mật khẩu thành công!");

        setTimeout(() => {
          navigate("/profile");
        }, 800);
      }
    } catch (err) {
      toast.error("Đổi mật khẩu thất bại!");
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <PageHeader />

      <div className="change-pass-page">
        <div className="change-pass-card">
          <h3 className="change-pass-title">Change Password</h3>
          <p className="change-pass-subtitle">
            Cập nhật mật khẩu mới để bảo mật tài khoản
          </p>

          <form onSubmit={handleSubmit}>
            <div className="change-pass-group">
              <label>Email</label>
              <input
                type="email"
                name="email"
                placeholder="you@email.com"
                value={form.email}
                onChange={handleChangeInput}
                required
              />
            </div>

            <div className="change-pass-group">
              <label>Old Password</label>
              <input
                type="password"
                name="oldPassword"
                placeholder="••••••••"
                value={form.oldPassword}
                onChange={handleChangeInput}
                required
              />
            </div>

            <div className="change-pass-group">
              <label>New Password</label>
              <input
                type="password"
                name="newPassword"
                placeholder="••••••••"
                value={form.newPassword}
                onChange={handleChangeInput}
                required
              />
            </div>

            <div className="change-pass-group">
              <label>Confirm Password</label>
              <input
                type="password"
                name="confirmPassword"
                placeholder="••••••••"
                value={form.confirmPassword}
                onChange={handleChangeInput}
                required
              />
            </div>

            <button
              className="change-pass-btn"
              type="submit"
              disabled={loading}
            >
              {loading ? "Processing..." : "Change Password"}
            </button>
          </form>
        </div>

        <ToastContainer />
      </div>
    </>
  );
}

export default ChangePasswordPage;
