import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { jwtDecode } from "jwt-decode";
import EditProfile from "./EditProfile";
import { DataContext } from "../DataContext";

function ProfilePage() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [activeTab, setActiveTab] = useState("activity");
  const [isEditing, setIsEditing] = useState(false);
  const [showProfile, setShowProfile] = useState(true); // toggle profile
  const [trips, setTrips] = useState([]);
  const { token } = useContext(DataContext);

  // L·∫•y email t·ª´ token
  let email = null;
  if (token) {
    try {
      const decoded = jwtDecode(token);
      email = decoded.sub;
    } catch (err) {
      console.error("Invalid token", err);
      navigate("/login");
    }
  } else {
    navigate("/login");
  }

  // G·ªçi API l·∫•y user profile
  useEffect(() => {
    const fetchProfile = async () => {
      if (!email) return;
      try {
        const res = await axios.get(
          `http://localhost:8080/api/auth/user/${email}`,
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        setUser(res.data);
      } catch (err) {
        console.error("Error fetching profile:", err);
      }
    };
    fetchProfile();
  }, [email, token]);



  const handleSaveProfile = (updatedUser) => {
    setUser(updatedUser);
    setIsEditing(false);
    alert("Profile updated successfully!");
  };

  const handleLogout = () => {
    localStorage.removeItem("trip-token");
    navigate("/login");
  };

  // H√†m th√™m Trip m·ªõi
  const handleAddTrip = async (e) => {
    e.preventDefault();
    const form = e.target;
    const formData = new FormData(form);

    try {
      const res = await axios.post("http://localhost:8080/api/trips", formData);
      setTrips([...trips, res.data]); // c·∫≠p nh·∫≠t list
      form.reset();
    } catch (err) {
      console.error("Error adding trip:", err);
      alert("Failed to add trip!");
    }
  };

  if (isEditing && user) {
    return (
      <EditProfile
        user={user}
        onSave={handleSaveProfile}
        onCancel={() => setIsEditing(false)}
      />
    );
  }

  if (!user) {
    return (
      <div className="d-flex justify-content-center align-items-center min-vh-100">
        <div className="spinner-border text-warning" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div
      className="container-fluid min-vh-100 py-5"
      style={{ background: "#fffaf5" }}
    >
      {/* HEADER */}
      <nav className="navbar navbar-expand-lg navbar-light bg-white shadow-sm mb-4 rounded-3 px-3">
        <a
          className="navbar-brand fw-bold"
          href="/"
          style={{ color: "#ff7a29" }}
        >
          üå∏ MyApp
        </a>
        <div className="ms-auto d-flex align-items-center gap-3">
          <span
            className="fw-semibold"
            style={{ cursor: "pointer" }}
            onClick={() => setShowProfile(!showProfile)}
          >
            {user.fullName}
          </span>
          <img
            src={user.avatar}
            alt="avatar"
            onClick={() => setShowProfile(!showProfile)}
            className="rounded-circle border border-2"
            style={{
              width: "40px",
              height: "40px",
              objectFit: "cover",
              cursor: "pointer",
            }}
          />
          <button
            className="btn btn-outline-danger btn-sm rounded-pill"
            onClick={handleLogout}
          >
            Logout
          </button>
        </div>
      </nav>

      {/* Profile Card - toggle show/hide */}
      {showProfile && (
        <div className="row justify-content-center mb-5">
          <div className="col-md-8">
            <div className="card border-0 shadow-lg rounded-4 overflow-hidden">
              {/* Header gradient */}
              <div
                className="p-4 text-center text-white"
                style={{
                  background: "linear-gradient(135deg, #ff7a29, #ff9e57)",
                }}
              >
                <img
                  src={user.avatar}
                  alt="avatar"
                  className="rounded-circle border border-4 border-white shadow mb-3"
                  style={{
                    width: "120px",
                    height: "120px",
                    objectFit: "cover",
                  }}
                />
                <h3 className="fw-bold mb-0">{user.fullName}</h3>
                <p className="mb-0">{user.email}</p>
              </div>

              <div className="card-body p-4">
                {/* Info */}
                <ul className="list-group list-group-flush mb-4">
                  <li className="list-group-item px-0">
                    <strong>üìû Phone:</strong> {user.phone}
                  </li>
                  <li className="list-group-item px-0">
                    <strong>üìç Address:</strong> {user.address}
                  </li>
                  <li className="list-group-item px-0">
                    <strong>‚öß Gender:</strong>{" "}
                    {user.gender === "MALE" ? "Male" : "Female"}
                  </li>
                  <li className="list-group-item px-0">
                    <strong>‚úÖ Status:</strong>{" "}
                    <span
                      className={`badge ${
                        user.status ? "bg-success" : "bg-danger"
                      }`}
                    >
                      {user.status ? "Active" : "Inactive"}
                    </span>
                  </li>
                </ul>

                {/* Actions */}
                <div className="d-flex gap-3">
                  <button
                    className="btn flex-fill text-white rounded-pill shadow"
                    style={{
                      background: "linear-gradient(135deg, #ff7a29, #ff9e57)",
                      border: "none",
                    }}
                    onClick={() => setIsEditing(true)}
                  >
                    Edit Profile
                  </button>
                  <button
                    className="btn flex-fill text-white rounded-pill shadow"
                    style={{
                      background: "linear-gradient(135deg, #29c2ff, #3d8bff)",
                      border: "none",
                    }}
                    onClick={() => navigate("/change-pass")}
                  >
                    Change Password
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Tabs */}
      <div className="row justify-content-center">
        <div className="col-md-10">
          <ul className="nav nav-pills justify-content-center mb-4">
            <li className="nav-item">
              <button
                className={`nav-link ${
                  activeTab === "activity" ? "active" : ""
                }`}
                onClick={() => setActiveTab("activity")}
              >
                Activity
              </button>
            </li>
            <li className="nav-item">
              <button
                className={`nav-link ${
                  activeTab === "settings" ? "active" : ""
                }`}
                onClick={() => setActiveTab("settings")}
              >
                Settings
              </button>
            </li>
            <li className="nav-item">
              <button
                className={`nav-link ${
                  activeTab === "friends" ? "active" : ""
                }`}
                onClick={() => setActiveTab("friends")}
              >
                Friends
              </button>
            </li>
          </ul>

          {/* Tab Content */}
          <div className="card border-0 shadow-sm rounded-4 p-4">
            {activeTab === "activity" && (
              <div>
                <div className="d-flex justify-content-between align-items-center mb-3">
                  <h5 className="fw-bold mb-0">Recent Trips</h5>
                  <button
                    className="btn btn-sm btn-primary rounded-pill"
                    data-bs-toggle="modal"
                    data-bs-target="#addTripModal"
                  >
                    + Add Trip
                  </button>
                </div>

                {/* Trip List */}
                <div className="row g-4">
                  {trips.map((trip) => {
                    const getStatusBadge = (status) => {
                      switch (status) {
                        case "PLANNED":
                          return (
                            <span className="badge bg-warning">{status}</span>
                          );
                        case "ONGOING":
                          return (
                            <span className="badge bg-primary">{status}</span>
                          );
                        case "COMPLETED":
                          return (
                            <span className="badge bg-success">{status}</span>
                          );
                        case "CANCELED":
                          return (
                            <span className="badge bg-danger">{status}</span>
                          );
                        default:
                          return (
                            <span className="badge bg-secondary">
                              Not clear
                            </span>
                          );
                      }
                    };

                    return (
                      <div className="col-md-4" key={trip.id}>
                        <div className="card h-100 shadow-sm border-0 rounded-4 overflow-hidden">
                          <img
                            src={trip.image}
                            alt={trip.name}
                            className="card-img-top"
                            style={{ height: "180px", objectFit: "cover" }}
                          />
                          <div className="card-body">
                            <div className="d-flex justify-content-between align-items-center mb-2">
                              <h5 className="card-title fw-bold mb-0">
                                {trip.name}
                              </h5>
                            </div>
                            <p>{getStatusBadge(trip.status)}</p>

                            {/* Th√™m ng√†y b·∫Øt ƒë·∫ßu v√† k·∫øt th√∫c */}
                            <p className="card-text mb-1">
                              <strong>B·∫Øt ƒë·∫ßu:</strong>{" "}
                              {new Date(trip.startDate).toLocaleDateString(
                                "vi-VN"
                              )}
                            </p>
                            <p className="card-text mb-1">
                              <strong>K·∫øt th√∫c:</strong>{" "}
                              {new Date(trip.endDate).toLocaleDateString(
                                "vi-VN"
                              )}
                            </p>

                            <p className="card-text mb-1">
                              <strong>Chi ph√≠ ∆∞·ªõc t√≠nh:</strong>{" "}
                              {trip.estimatedCost.toLocaleString()} ‚Ç´
                            </p>
                            <p className="card-text">
                              <strong>Chi ph√≠ th·ª±c t·∫ø:</strong>{" "}
                              {trip.actualCost.toLocaleString()} ‚Ç´
                            </p>

                            {/* N√∫t detail */}
                            <button
                              className="btn btn-primary mt-2"
                              onClick={() => navigate(`/trips/${trip.id}`)}
                            >
                              Chi ti·∫øt
                            </button>
                          </div>
                        </div>
                      </div>
                    );
                  })}
                </div>

                {/* Modal Add Trip */}
                <div
                  className="modal fade"
                  id="addTripModal"
                  tabIndex="-1"
                  aria-hidden="true"
                >
                  <div className="modal-dialog modal-lg modal-dialog-centered">
                    <div className="modal-content">
                      <div className="modal-header">
                        <h5 className="modal-title">Add New Trip</h5>
                        <button
                          type="button"
                          className="btn-close"
                          data-bs-dismiss="modal"
                          aria-label="Close"
                        ></button>
                      </div>
                      <div className="modal-body">
                        <form
                          id="tripForm"
                          onSubmit={handleAddTrip}
                          encType="multipart/form-data"
                        >
                          <div className="row g-3">
                            <div className="col-md-6">
                              <label className="form-label">Name</label>
                              <input
                                type="text"
                                className="form-control"
                                name="name"
                                required
                              />
                            </div>
                            <div className="col-md-6">
                              <label className="form-label">Depart</label>
                              <input
                                type="text"
                                className="form-control"
                                name="depart"
                                required
                              />
                            </div>
                            <div className="col-md-6">
                              <label className="form-label">Destination</label>
                              <input
                                type="text"
                                className="form-control"
                                name="destination"
                                required
                              />
                            </div>
                            <div className="col-md-6">
                              <label className="form-label">
                                Estimated Cost
                              </label>
                              <input
                                type="number"
                                className="form-control"
                                name="estimatedCost"
                                required
                              />
                            </div>
                            <div className="col-md-12">
                              <label className="form-label">Image URL</label>
                              <input
                                type="file"
                                className="form-control"
                                name="file"
                              />
                            </div>
                          </div>
                          <div className="modal-footer mt-4">
                            <button
                              type="button"
                              className="btn btn-secondary"
                              data-bs-dismiss="modal"
                            >
                              Cancel
                            </button>
                            <button
                              type="submit"
                              className="btn btn-primary"
                              data-bs-dismiss="modal"
                            >
                              Save Trip
                            </button>
                          </div>
                        </form>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}
            {activeTab === "settings" && (
              <div>
                <h5 className="fw-bold mb-3">Settings</h5>
                <ul className="list-group list-group-flush">
                  <li className="list-group-item">üîî Notification Settings</li>
                  <li className="list-group-item">üåô Dark / Light Mode</li>
                  <li className="list-group-item">üõ°Ô∏è Privacy Controls</li>
                </ul>
              </div>
            )}

            {activeTab === "friends" && (
              <div>
                <h5 className="fw-bold mb-3">Friends</h5>
                <p>Danh s√°ch b·∫°n b√® s·∫Ω hi·ªÉn th·ªã ·ªü ƒë√¢y.</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProfilePage;
