import React, { useContext, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { Link, useNavigate } from "react-router-dom";
import { DataContext } from "../DataContext";
import PageHeader from "../components/page/PageHeader";
import "../css/page/LoginPage.css";

function LoginPage() {
  const navigate = useNavigate();
  const [data, setData] = useState({ email: "", password: "" });
  const [loading, setLoading] = useState(false);

  const { setToken, setUser } = useContext(DataContext);

  const handleChangeInput = (e) => {
    const { name, value } = e.target;
    setData({ ...data, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const res = await axios.post(
        "http://localhost:8080/api/auth/login",
        data
      );

      const accessToken =
        res.data.accessToken ||
        res.data.access_token ||
        res.data.token;

      if (!accessToken) {
        toast.error("Token kh√¥ng h·ª£p l·ªá t·ª´ server");
        return;
      }

      setToken(accessToken);

      const meRes = await axios.get(
        "http://localhost:8080/api/auth/user/me",
        {
          headers: {
            Authorization: `Bearer ${accessToken}`,
          },
        }
      );

      setUser(meRes.data.data || meRes.data);

      toast.success("ƒêƒÉng nh·∫≠p th√†nh c√¥ng");
      navigate("/");
    } catch {
      toast.error("Sai email ho·∫∑c m·∫≠t kh·∫©u");
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <PageHeader />

      <div className="login-page">
        <div className="login-card">
          <h2 className="login-title">Welcome Back üëã</h2>
          <p className="login-subtitle">
            ƒêƒÉng nh·∫≠p ƒë·ªÉ ti·∫øp t·ª•c mua s·∫Øm
          </p>

          <form onSubmit={handleSubmit}>
            <div className="login-group">
              <label>Email</label>
              <input
                type="email"
                name="email"
                placeholder="you@email.com"
                onChange={handleChangeInput}
                required
              />
            </div>

            <div className="login-group">
              <label>M·∫≠t kh·∫©u</label>
              <input
                type="password"
                name="password"
                placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                onChange={handleChangeInput}
                required
              />
            </div>

            {/* üîπ FORGOT PASSWORD */}
            <div className="login-forgot">
              <Link to="/forgot-password">
                Qu√™n m·∫≠t kh·∫©u?
              </Link>
            </div>

            <button
              type="submit"
              className="login-btn"
              disabled={loading}
            >
              {loading ? "ƒêang ƒëƒÉng nh·∫≠p..." : "ƒêƒÉng nh·∫≠p"}
            </button>
          </form>

          <p className="login-footer">
            Ch∆∞a c√≥ t√†i kho·∫£n?{" "}
            <Link to="/register">ƒêƒÉng k√Ω</Link>
          </p>
        </div>
      </div>
    </>
  );
}

export default LoginPage;
