// src/pages/MyOrdersPage.js
import React, { useEffect, useState } from "react";
import "../css/oders/MyOrdersPage.css";
import axios from "axios";

// ⭐ Thêm header user
import PageHeader from "../components/";

export default function MyOrdersPage() {

  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("trip-token");

    axios.get("http://localhost:8080/api/orders/my", {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => {
        console.log("My Orders:", res.data);
        setOrders(res.data || []);
      })
      .catch(err => {
        console.log("Error:", err.response?.data || err.message);
      });
  }, []);

  const handleCancel = (id) => {
    const token = localStorage.getItem("trip-token");

    axios.put(
      `http://localhost:8080/api/orders/${id}/cancel`,
      {},
      { headers: { Authorization: `Bearer ${token}` } }
    )
      .then(() => {
        alert("Hủy đơn thành công");
        setOrders(prev =>
          prev.map(o =>
            o.id === id ? { ...o, status: "CANCELLED" } : o
          )
        );
      })
      .catch(() => alert("Không thể hủy đơn"));
  };

  return (
    <>
      {/* ⭐ Header */}
      <PageHeader />

      <div className="order-container">
        <h2>Đơn hàng của bạn</h2>

        {orders.map(order => (
          <div className="order-card" key={order.id}>

            <div className="order-header">
              <span className="order-id">Mã đơn #{order.id}</span>
              <span className={`order-status status-${order.status}`}>
                {translateStatus(order.status)}
              </span>
            </div>

            <div className="order-products">
              {order.items?.map(item => (
                <div className="order-product" key={item.productId}>
                  <img
                    src={item.imageUrl || "https://via.placeholder.com/70"}
                    alt=""
                  />
                  <div>
                    <div className="name">{item.productName}</div>
                    <div className="qty">x{item.quantity}</div>
                    <div className="price">
                      {(item.price).toLocaleString()} ₫
                    </div>
                  </div>
                </div>
              ))}
            </div>

            <div className="order-footer">

              <div className="order-total">
                Tổng: {order.totalPrice.toLocaleString()} ₫
              </div>

              {(order.status === "PENDING" || order.status === "CONFIRMED") && (
                <button
                  onClick={() => handleCancel(order.id)}
                  className="btn-cancel"
                >
                  Hủy đơn
                </button>
              )}

            </div>
          </div>
        ))}
      </div>
    </>
  );
}


function translateStatus(status) {
  switch (status) {
    case "PENDING": return "Chờ xác nhận";
    case "CONFIRMED": return "Đã xác nhận";
    case "SHIPPING": return "Đang giao";
    case "COMPLETED": return "Giao thành công";
    case "CANCELLED": return "Đã hủy";
    default: return status;
  }
}
