import React, { useState, useEffect } from "react";
import "./PageHeader.css";
import { useNavigate } from "react-router-dom";

export default function PageHeader({ user, onLogout }) {
  const navigate = useNavigate();
  const [openMenu, setOpenMenu] = useState(false);
  const [cartCount, setCartCount] = useState(0);

  const toggleMenu = () => setOpenMenu(!openMenu);

  // c·∫≠p nh·∫≠t s·ªë l∆∞·ª£ng khi user ho·∫∑c localStorage thay ƒë·ªïi
  const updateCartCount = () => {
    if (!user) {
      setCartCount(0);
      return;
    }

    const key = `cart_${user.email}`;
    const cart = JSON.parse(localStorage.getItem(key)) || [];
    const total = cart.reduce((s, i) => s + i.quantity, 0);

    setCartCount(total);
  };

  // ch·∫°y m·ªói khi user thay ƒë·ªïi
  useEffect(() => {
    updateCartCount();
  }, [user]);


  // l·∫Øng nghe storage thay ƒë·ªïi (khi addToCart)
  useEffect(() => {
    const handleStorage = () => updateCartCount();
    window.addEventListener("storage", handleStorage);

    return () => window.removeEventListener("storage", handleStorage);
  }, []);

  const goToCart = () => {
    if (!user) {
      alert("B·∫°n c·∫ßn ƒëƒÉng nh·∫≠p ƒë·ªÉ xem gi·ªè h√†ng!");
      navigate("/login");
      return;
    }
    navigate("/cart");
  };

  return (
    <>
      <nav className="cp-header">

        <div className="cp-logo" onClick={() => navigate("/")}>
          Cellphone<span>S</span>
        </div>

        <input className="cp-search" placeholder="T√¨m ki·∫øm..." />

        <div className="cp-icons">
          
          {
            user ?
              <img
                src={user.avatar}
                onClick={toggleMenu}
                className="avatar"
              />
            :
              <button onClick={() => navigate("/login")}>ƒêƒÉng nh·∫≠p</button>
          }

          <div className="cp-cart" onClick={goToCart}>
            üõí
            {cartCount > 0 && (
              <span className="cp-cart-count">{cartCount}</span>
            )}
          </div>

        </div>
      </nav>
    </>
  );
}
