import React, { useState } from "react";
import axios from "axios";
import { toast, ToastContainer } from "react-toastify";

// ⭐ import Header giống các trang khác
import PageHeader from "../admin";

const DEFAULT_AVATAR =
  "https://cdn-icons-png.flaticon.com/512/149/149071.png";

function EditProfile({ user, onSave, onCancel }) {
  const [formData, setFormData] = useState({
    email: user.email,
    fullName: user.fullName,
    phone: user.phone,
    address: user.address,
    gender: user.gender,
    file: null,
    avatarPreview: user.avatar ? user.avatar : DEFAULT_AVATAR,
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "file" && files.length > 0) {
      const file = files[0];
      setFormData({
        ...formData,
        file: file,
        avatarPreview: URL.createObjectURL(file),
      });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const data = new FormData();
      data.append("email", formData.email);
      data.append("fullName", formData.fullName);
      data.append("phone", formData.phone);
      data.append("address", formData.address);
      data.append("gender", formData.gender);

      if (formData.file) {
        data.append("file", formData.file);
      }

      const token = localStorage.getItem("trip-token");

      const res = await axios.put(
        "http://localhost:8080/api/auth/profile",
        data,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (res.status === 200) {
        toast.success("Cập nhật hồ sơ thành công!");

        onSave({
          ...formData,
          avatar: formData.avatarPreview,
        });
      }
    } catch (err) {
      console.log("err:", err);
      toast.error(err.response?.data?.message || "Cập nhật thất bại!");
    }
  };

  return (
    <>
      {/* Header */}
      <PageHeader />

      <div className="container py-5">
        <div className="card shadow border-0 rounded-4 p-4">
          <h4 className="mb-3" style={{ color: "#ff7a29" }}>
            Edit Profile
          </h4>
          <form onSubmit={handleSubmit}>
            <div className="row g-3">

              {/* Avatar Preview */}
              <div className="col-12 text-center mb-3">
                <img
                  src={formData.avatarPreview}
                  alt="avatar preview"
                  className="rounded-circle shadow"
                  style={{
                    width: "120px",
                    height: "120px",
                    objectFit: "cover",
                  }}
                />
              </div>

              <div className="col-md-6">
                <label className="form-label">Full Name</label>
                <input
                  type="text"
                  name="fullName"
                  className="form-control"
                  value={formData.fullName}
                  onChange={handleChange}
                />
              </div>

              <div className="col-md-6">
                <label className="form-label">Email</label>
                <input
                  type="email"
                  name="email"
                  readOnly
                  className="form-control"
                  value={formData.email}
                />
              </div>

              <div className="col-md-6">
                <label className="form-label">Phone</label>
                <input
                  type="text"
                  name="phone"
                  className="form-control"
                  value={formData.phone}
                  onChange={handleChange}
                />
              </div>

              <div className="col-md-6">
                <label className="form-label">Address</label>
                <input
                  type="text"
                  name="address"
                  className="form-control"
                  value={formData.address}
                  onChange={handleChange}
                />
              </div>

              <div className="col-md-6">
                <label className="form-label">Gender</label>
                <select
                  name="gender"
                  className="form-select"
                  value={formData.gender}
                  onChange={handleChange}
                >
                  <option value="MALE">Male</option>
                  <option value="FEMALE">Female</option>
                </select>
              </div>

              <div className="col-md-6">
                <label className="form-label">Avatar</label>
                <input
                  type="file"
                  name="file"
                  className="form-control"
                  onChange={handleChange}
                />
              </div>
            </div>

            <div className="mt-4 d-flex gap-3">
              <button
                className="btn text-white rounded-pill shadow px-4"
                style={{
                  background: "linear-gradient(135deg, #ff7a29, #ff9e57)",
                  border: "none",
                }}
              >
                Save Changes
              </button>

              <button
                type="button"
                className="btn btn-outline-secondary rounded-pill px-4"
                onClick={onCancel}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
        <ToastContainer />
      </div>
    </>
  );
}

export default EditProfile;
