import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import AdminHeader from "../page/AdminHeader";

export default function AddProduct() {
  const navigate = useNavigate();
  const token = localStorage.getItem("adminToken");

  const [form, setForm] = useState({
    name: "",
    brand: "",
    price: "",
    quantityInStock: "",
    screenSize: "",
    ram: "",
    storage: "",
    chipset: "",
    camera: "",
    battery: "",
    os: "",
    color: "",
    description: "",
  });

  const [images, setImages] = useState([]);
  const [preview, setPreview] = useState([]);

  // Nếu không có token → Out luôn
  if (!token) {
    navigate("/admin/login");
    return null;
  }

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    setImages([...images, ...files]);
    const previewUrls = files.map((file) => URL.createObjectURL(file));
    setPreview([...preview, ...previewUrls]);
  };

  const removeImage = (index) => {
    const newImgs = [...images];
    const newPrev = [...preview];
    newImgs.splice(index, 1);
    newPrev.splice(index, 1);
    setImages(newImgs);
    setPreview(newPrev);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    Object.keys(form).forEach((key) => data.append(key, form[key]));
    images.forEach((img) => data.append("images", img));

    try {
      await axios.post("http://localhost:8080/api/admin/products", data, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data",
        },
      });

      alert("Thêm sản phẩm thành công!");
      navigate("/admin/products");

    } catch (error) {
      if (error.response?.status === 401) {
        localStorage.removeItem("adminToken");
        navigate("/admin/login");
      }
      alert("Lỗi khi thêm sản phẩm!");
      console.error(error);
    }
  };

  return (
    <>
      <AdminHeader />
      <div style={{ height: 70 }}></div>

      <div style={styles.wrapper}>
        <h2 style={styles.pageTitle}>Thêm sản phẩm mới</h2>

        <form onSubmit={handleSubmit} style={styles.formContainer}>

          <div style={styles.leftCol}>
            <div style={styles.card}>
              <h3 style={styles.cardTitle}>Thông tin cơ bản</h3>

              <label style={styles.label}>Tên sản phẩm</label>
              <input name="name" onChange={handleChange} style={styles.input} required />

              <label style={styles.label}>Thương hiệu</label>
              <input name="brand" onChange={handleChange} style={styles.input} required />

              <label style={styles.label}>Màu sắc</label>
              <input name="color" onChange={handleChange} style={styles.input} required />

              <label style={styles.label}>Mô tả</label>
              <textarea
                name="description"
                onChange={handleChange}
                style={styles.textarea}
                required
              />
            </div>

            <div style={styles.card}>
              <h3 style={styles.cardTitle}>Thông số kỹ thuật</h3>

              {[
                "screenSize",
                "ram",
                "storage",
                "chipset",
                "camera",
                "battery",
                "os",
              ].map((key) => (
                <input
                  key={key}
                  placeholder={key}
                  name={key}
                  onChange={handleChange}
                  style={styles.input}
                />
              ))}
            </div>
          </div>

          <div style={styles.rightCol}>
            <div style={styles.card}>
              <h3 style={styles.cardTitle}>Giá & Kho</h3>

              <label style={styles.label}>Giá</label>
              <input
                name="price"
                type="number"
                onChange={handleChange}
                style={styles.input}
                required
              />

              <label style={styles.label}>Số lượng tồn</label>
              <input
                name="quantityInStock"
                type="number"
                onChange={handleChange}
                style={styles.input}
                required
              />
            </div>

            {/* Upload images */}
            <div style={styles.card}>
              <h3 style={styles.cardTitle}>Hình ảnh</h3>

              <label style={styles.uploadLabel}>
                Chọn ảnh
                <input type="file" multiple accept="image/*" onChange={handleImageUpload} style={{ display: "none" }} />
              </label>

              <div style={styles.previewContainer}>
                {preview.map((img, index) => (
                  <div key={index} style={styles.previewBox}>
                    <img src={img} style={styles.previewImg} alt="" />
                    <button type="button" style={styles.removeImg} onClick={() => removeImage(index)}>
                      x
                    </button>
                  </div>
                ))}
              </div>
            </div>

            <button type="submit" style={styles.submitBtn}>
              Thêm sản phẩm
            </button>
          </div>

        </form>
      </div>
    </>
  );
}



const styles = {
  wrapper: { padding: "25px 20px", maxWidth: 1350, margin: "auto" },

  pageTitle: { fontSize: 26, fontWeight: 600, color: "#333" },

  headerTop: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: 25,
  },

  addBtn: {
    display: "flex",
    alignItems: "center",
    gap: 6,
    background: "#ee4d2d",
    color: "#fff",
    textDecoration: "none",
    padding: "10px 16px",
    borderRadius: 6,
    fontSize: 14,
  },

  tableHeader: {
    display: "flex",
    padding: "12px 10px",
    background: "#fafafa",
    border: "1px solid #eee",
    borderRadius: 6,
    fontWeight: 600,
    color: "#555",
    marginBottom: 10,
  },

  tableRow: {
    display: "flex",
    padding: "12px 10px",
    borderBottom: "1px solid #eee",
    alignItems: "center",
    transition: "0.2s",
  },

  rowCell: { fontSize: 14, color: "#333" },

  productCell: {
    display: "flex",
    alignItems: "center",
    gap: 12,
  },

  thumbnail: {
    width: 65,
    height: 65,
    objectFit: "cover",
    borderRadius: 6,
    border: "1px solid #eee",
  },

  skuText: { fontSize: 12, color: "#888" },

  actionLink: {
    display: "block",
    marginBottom: 4,
    color: "#007bff",
    textDecoration: "none",
    cursor: "pointer",
  },
};
