import React, { useContext } from "react";
import "../css/page/CartPage.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { DataContext } from "../DataContext";
import PageHeader from "../components/page/PageHeader";

export default function CartPage() {
  const navigate = useNavigate();
  const { user, token, cart, fetchCart } = useContext(DataContext);

  // Cập nhật số lượng qua API
  const updateQty = async (cartItemId, newQty) => {
    if (newQty < 1) return;
    try {
      await axios.put(`http://localhost:8080/api/cart/update/${cartItemId}?quantity=${newQty}`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchCart(); 
    } catch (err) { alert("Lỗi cập nhật!"); }
  };

  // Xóa sản phẩm qua API
  const removeItem = async (cartItemId) => {
    if (!window.confirm("Xóa sản phẩm này?")) return;
    try {
      await axios.delete(`http://localhost:8080/api/cart/${cartItemId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchCart();
    } catch (err) { alert("Lỗi xóa sản phẩm!"); }
  };

  // Tính tổng tiền dựa trên cấu trúc item.product.price
  const total = cart.reduce((sum, i) => sum + (i.product?.price * i.quantity), 0);

  const checkout = () => {
    if (cart.length === 0) {
      alert("Giỏ hàng rỗng!");
      return;
    }
    navigate("/checkout");
  };

  return (
    <div className="cart-container">
      <PageHeader />
      <h2>Giỏ hàng</h2>
      <div className="cart-grid">
        <div className="cart-items">
          {cart.length === 0 && <p>Giỏ hàng trống</p>}
          {cart.map((item) => (
            <div className="cart-item" key={item.id}>
              {/* Lưu ý: item.product.imageUrls */}
              <img src={item.product?.imageUrls?.[0]} alt={item.product?.name} />
              <div className="info">
                <div className="name">{item.product?.name}</div>
                <div className="price">{item.product?.price.toLocaleString()} ₫</div>
                <div className="qty-box">
                  <button onClick={() => updateQty(item.id, item.quantity - 1)}>-</button>
                  <span>{item.quantity}</span>
                  <button onClick={() => updateQty(item.id, item.quantity + 1)}>+</button>
                </div>
                <button className="remove" onClick={() => removeItem(item.id)}>Xóa</button>
              </div>
              <div className="subtotal">
                {(item.product?.price * item.quantity).toLocaleString()} ₫
              </div>
            </div>
          ))}
        </div>

        <div className="cart-summary">
          <h3>Tổng tiền</h3>
          <div className="sum-row">
            <span>Tạm tính:</span>
            <strong>{total.toLocaleString()} ₫</strong>
          </div>
          <button className="btn-checkout" onClick={checkout}>Đặt hàng</button>
        </div>
      </div>
    </div>
  );
}
