import React, { useContext, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { Link, useNavigate } from "react-router-dom";
import { DataContext } from "../DataContext";
import ProfileHeader from "../components/page/PageHeader";

function LoginPage() {
  const navigate = useNavigate();
  const [data, setData] = useState({ email: "", password: "" });
  const [loading, setLoading] = useState(false);
  const { setToken } = useContext(DataContext);

  const handleChangeInput = (e) => {
    const { name, value } = e.target;
    setData({ ...data, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const res = await axios.post("http://localhost:8080/api/auth/login", data);

      const accessToken =
        res.data.accessToken ||
        res.data.access_token ||
        res.data.token;

      if (!accessToken) {
        toast.error("Token không hợp lệ từ server");
        return;
      }

      localStorage.setItem("trip-token", accessToken);
      setToken(accessToken);

      // ⭐⭐⭐ GET USER FROM DB (THÊM DÒNG NÀY)
      const meRes = await axios.get("http://localhost:8080/api/auth/user/me", {
        headers: { Authorization: `Bearer ${accessToken}` },
      });

      localStorage.setItem("user-data", JSON.stringify(meRes.data.data));

      toast.success("Đăng nhập thành công");
      navigate("/");

    } catch (err) {
      toast.error("Sai email hoặc mật khẩu");
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <ProfileHeader />

      <div
        className="bg-light min-vh-100 d-flex justify-content-center align-items-center"
        style={{ background: "#fffaf5" }}
      >
        <div className="col-md-5 col-lg-4">
          <div className="card border-0 shadow-lg rounded-4">
            <div className="card-body p-5">

              <h2 className="text-center fw-bold mb-2" style={{ color: "#ff7a29" }}>
                Welcome Back
              </h2>

              <form onSubmit={handleSubmit}>

                <div className="mb-3">
                  <label className="form-label fw-semibold">Email address</label>
                  <input name="email" type="email" onChange={handleChangeInput}
                    className="form-control" required />
                </div>

                <div className="mb-3">
                  <label className="form-label fw-semibold">Password</label>
                  <input
                    type="password"
                    name="password"
                    onChange={handleChangeInput}
                    className="form-control"
                    required
                  />
                </div>

                <button type="submit" disabled={loading}
                  className="btn btn-primary w-100">
                  {loading ? "Logging in..." : "Login"}
                </button>

              </form>

            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default LoginPage;
