import React, { useContext, useState, useMemo } from "react";
import { Link, useNavigate } from "react-router-dom";
import { DataContext } from "../../DataContext";
import "./PageHeader.css";

const DEFAULT_AVATAR =
  "https://cdn-icons-png.flaticon.com/512/149/149071.png";

export default function PageHeader() {
  const navigate = useNavigate();
  const { user, logout, cart } = useContext(DataContext);
  const [openMenu, setOpenMenu] = useState(false);

  const toggleMenu = () => setOpenMenu((prev) => !prev);

  const cartCount = Array.isArray(cart)
    ? cart.reduce((sum, item) => sum + (item.quantity || 0), 0)
    : 0;

  const avatarUrl = useMemo(() => {
    if (!user?.avatar) return null;
    return `${user.avatar}?v=${user.updatedAt || user.id || "1"}`;
  }, [user?.avatar, user?.updatedAt]);

  const handleCartClick = () => {
    if (!user) {
      alert("B·∫°n c·∫ßn ƒëƒÉng nh·∫≠p ƒë·ªÉ xem gi·ªè h√†ng!");
      navigate("/login");
    } else {
      navigate("/cart");
    }
  };

  return (
    <header className="uh-header">
      <div className="uh-container">
        <div className="uh-logo" onClick={() => navigate("/")}>
          Mobile<span>S</span>tore
        </div>

        <nav className="uh-nav">
          <Link to="/">Trang ch·ªß</Link>
          <Link to="/products">S·∫£n ph·∫©m</Link>
          <Link to="/news">Tin t·ª©c</Link>
          <Link to="/contact">Li√™n h·ªá</Link>
        </nav>

        <div className="uh-actions">
          {user ? (
            <div className="uh-user">
              <div className="uh-avatar" onClick={toggleMenu}>
                {avatarUrl ? (
                  <img
                    src={avatarUrl}
                    alt="avatar"
                    onError={(e) => {
                      e.currentTarget.src = DEFAULT_AVATAR;
                    }}
                  />
                ) : (
                  user.fullName?.charAt(0)?.toUpperCase() || "U"
                )}
              </div>

              {openMenu && (
                <div className="uh-menu">
                  <div onClick={() => navigate("/profile")}>üë§ Th√¥ng tin</div>
                  <div onClick={() => navigate("/my-orders")}>üì¶ ƒê∆°n h√†ng</div>
                  <div onClick={logout}>üö™ ƒêƒÉng xu·∫•t</div>
                </div>
              )}
            </div>
          ) : (
            <button className="uh-login" onClick={() => navigate("/login")}>
              ƒêƒÉng nh·∫≠p
            </button>
          )}

          <div
            className="uh-cart"
            onClick={handleCartClick}
            style={{ cursor: "pointer", position: "relative" }}
          >
            <span style={{ fontSize: "24px" }}>üõí</span>
            {cartCount > 0 && <span className="uh-cart-count">{cartCount}</span>}
          </div>
        </div>
      </div>
    </header>
  );
}
