import React, { useState, useEffect } from "react";
import "./PageHeader.css";
import { useNavigate } from "react-router-dom";

export default function PageHeader({ user, onLogout }) {
  const navigate = useNavigate();
  const [openMenu, setOpenMenu] = useState(false);

  // √©p re-render khi localStorage thay ƒë·ªïi
  const [refresh, setRefresh] = useState(false);

  const toggleMenu = () => setOpenMenu(!openMenu);

  // gi·ªè h√†ng theo user
  const getCartCount = () => {
    if (!user) return 0;           // ch∆∞a login ‚Üí 0

    const key = `cart_${user.email}`;
    const cart = JSON.parse(localStorage.getItem(key)) || [];
    return cart.reduce((sum, item) => sum + item.quantity, 0);
  };

  // l·∫Øng nghe localStorage thay ƒë·ªïi
  useEffect(() => {
    const handle = () => setRefresh(prev => !prev);
    window.addEventListener("storage", handle);
    return () => window.removeEventListener("storage", handle);
  }, []);

  const goToCart = () => {
    if (!user) {
      alert("B·∫°n c·∫ßn ƒëƒÉng nh·∫≠p ƒë·ªÉ xem gi·ªè h√†ng!");
      navigate("/login");
      return;
    }
    navigate("/cart");
  };

  return (
    <>
      <div className="cp-topbar">
        <div className="cp-content">
          <div className="text-white small">Hotline: 1800 2097</div>
          <div className="d-flex gap-3">
            <span className="text-white small">H·ªá th·ªëng c·ª≠a h√†ng</span>
            <span className="text-white small">üöö Giao h√†ng nhanh trong 2 gi·ªù</span>
          </div>
        </div>
      </div>

      <nav className="cp-header shadow-sm">
        <div className="cp-content">

          <div className="fw-bold cp-logo" onClick={() => navigate("/")}>
            Cellphone<span>S</span>
          </div>

          <div className="flex-grow-1 px-3">
            <input className="form-control cp-search" placeholder="B·∫°n mu·ªën t√¨m g√¨?" />
          </div>

          <div className="d-flex align-items-center gap-4 cp-icons">

            {user ? (
              <div className="position-relative">

                <img
                  src={user.avatar}
                  onClick={toggleMenu}
                  className="rounded-circle"
                  style={{ width: 38, height: 38, cursor:"pointer" }}
                />

                {openMenu && (
                  <div className="user-menu shadow-sm">
                    <div onClick={() => navigate("/profile")}>üë§ Th√¥ng tin t√†i kho·∫£n</div>
                    <div onClick={() => navigate("/my-orders")}>üì¶ ƒê∆°n h√†ng c·ªßa t√¥i</div>
                    <div onClick={onLogout}>üö™ ƒêƒÉng xu·∫•t</div>
                  </div>
                )}
              </div>
            ) : (
              <button className="btn btn-sm btn-danger" onClick={() => navigate("/login")}>
                ƒêƒÉng nh·∫≠p
              </button>
            )}

            {/* üõí CART ICON */}
            <div className="cp-cart" onClick={goToCart}>
              üõí 
              {getCartCount() > 0 && (
                <span className="cp-cart-count">{getCartCount()}</span>
              )}
            </div>
          </div>
        </div>
      </nav>
    </>
  );
}
