import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../css/page/ProductDetail.css";
import ProfileHeader from "../components/page/PageHeader";

export default function ProductDetail() {

  const { id } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:8080/api/products/${id}`)
      .then(res => setProduct(res.data.data))
      .catch(err => console.log(err));
  }, [id]);


  if (!product)
    return (
      <div className="loading">
        <div className="spinner-border text-danger"></div>
      </div>
    );


  return (
    <div className="pd-container">

      <ProfileHeader />

      <div className="pd-content">

        {/* LEFT IMAGE */}
        <div className="pd-left">
          <img
            src={product.images[0]?.imageUrl}
            alt={product.name}
            className="pd-main-img"
          />
        </div>

        {/* RIGHT INFO */}
        <div className="pd-right">

          <h2>{product.name}</h2>

          <span className="pd-price">
            {product.price.toLocaleString()} ‚Ç´
          </span>

          <div className="pd-promo">
            üéÅ Khuy·∫øn m√£i h·∫•p d·∫´n t·∫°i CellphoneS!
          </div>

          <button className="pd-buy">
            Mua ngay
          </button>

          <button className="pd-add">
            Th√™m v√†o gi·ªè h√†ng
          </button>

          {/* spec */}
          <ul className="pd-spec">
            <li>üì± M√†n h√¨nh: {product.screenSize}</li>
            <li>‚ö° Chip: {product.chipset}</li>
            <li>üîã Pin: {product.battery}</li>
            <li>üì∏ Camera: {product.camera}</li>
            <li>üíæ RAM: {product.ram}</li>
            <li>üíΩ ROM: {product.storage}</li>
          </ul>

        </div>

      </div>

      {/* Long description */}
      <div className="pd-desc">
        <h3>M√¥ t·∫£ s·∫£n ph·∫©m</h3>
        <p>{product.description}</p>
      </div>
    </div>
  );
}
