import React, { useContext, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { Link, useNavigate } from "react-router-dom";
import { DataContext } from "../DataContext";
import ProfileHeader from "../components/page/PageHeader"; // <-- TH√äM

function LoginPage() {
  const navigate = useNavigate();
  const [data, setData] = useState({ email: "", password: "" });
  const [loading, setLoading] = useState(false);
  const { setToken } = useContext(DataContext);

  const handleChangeInput = (e) => {
    const { name, value } = e.target;
    setData({ ...data, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const res = await axios.post("http://localhost:8080/api/auth/login", data);

      const accessToken =
        res.data.accessToken ||
        res.data.access_token ||
        res.data.token;

      if (!accessToken) {
        toast.error("Token kh√¥ng h·ª£p l·ªá t·ª´ server");
        return;
      }

      localStorage.setItem("trip-token", accessToken);
      setToken(accessToken);

      // ==============================
      // üî• FIX: l·∫•y user ƒë√∫ng t·ª´ backend
      // ==============================
      const meRes = await axios.get(
        "http://localhost:8080/api/auth/user/me",
        {
          headers: { Authorization: `Bearer ${accessToken}` }
        }
      );

      localStorage.setItem("user-data", JSON.stringify(meRes.data.data));
      // ==============================

      toast.success("ƒêƒÉng nh·∫≠p th√†nh c√¥ng");
      navigate("/");

    } catch (err) {
      toast.error("Sai email ho·∫∑c m·∫≠t kh·∫©u");
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      {/* HEADER */}
      <ProfileHeader />  

      <div
        className="bg-light min-vh-100 d-flex justify-content-center align-items-center"
        style={{ background: "#fffaf5" }}
      >
        <div className="col-md-5 col-lg-4">
          <div className="card border-0 shadow-lg rounded-4">
            <div className="card-body p-5">

              <h2
                className="text-center fw-bold mb-2"
                style={{ color: "#ff7a29" }}
              >
                Welcome Back
              </h2>

              <p className="text-center text-muted mb-5">
                Please login to continue
              </p>

              <form onSubmit={handleSubmit}>

                <div className="mb-3">
                  <label htmlFor="email" className="form-label fw-semibold">
                    Email address
                  </label>
                  <div className="input-group">
                    <span className="input-group-text bg-white border-end-0 rounded-start-pill">
                      <i className="bi bi-envelope-fill text-muted"></i>
                    </span>
                    <input
                      type="email"
                      className="form-control border-start-0 rounded-end-pill px-3"
                      id="email"
                      name="email"
                      onChange={handleChangeInput}
                      placeholder="Enter your email"
                      required
                    />
                  </div>
                </div>

                <div className="mb-3">
                  <label htmlFor="password" className="form-label fw-semibold">
                    Password
                  </label>
                  <div className="input-group">
                    <span className="input-group-text bg-white border-end-0 rounded-start-pill">
                      <i className="bi bi-lock-fill text-muted"></i>
                    </span>
                    <input
                      type="password"
                      className="form-control border-start-0 rounded-end-pill px-3"
                      id="password"
                      name="password"
                      onChange={handleChangeInput}
                      placeholder="Enter your password"
                      required
                    />
                  </div>
                </div>

                <div className="d-flex justify-content-end mb-4">
                  <Link
                    to="/forgot-password"
                    className="text-decoration-none small"
                  >
                    Forgot Password?
                  </Link>
                </div>

                <div className="d-grid">
                  <button
                    type="submit"
                    className="btn btn-lg text-white rounded-pill shadow"
                    style={{
                      background: "linear-gradient(135deg, #ff7a29, #ff9e57)",
                      border: "none",
                    }}
                    disabled={loading}
                  >
                    {loading ? "Logging in..." : "Login"}
                  </button>
                </div>
              </form>

              <p className="text-center text-muted mt-4 mb-0">
                Don‚Äôt have an account?{" "}
                <Link
                  to="/register"
                  className="fw-semibold text-decoration-none"
                  style={{ color: "#ff7a29" }}
                >
                  Sign up
                </Link>
              </p>

            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default LoginPage;
