import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

import {
  Package,
  ShoppingBag,
  Tag,
  BarChart2,
} from "lucide-react";

import AdminHeader from "./AdminHeader";

export default function HomeAdmin() {

  const navigate = useNavigate();
  const adminToken = localStorage.getItem("adminToken");

  const [products, setProducts] = useState([]);

  useEffect(() => {
    const config = adminToken
      ? { headers: { Authorization: `Bearer ${adminToken}` } }
      : {};

    axios.get("http://localhost:8080/api/admin/products", config)
      .then(res => {
        const data = Array.isArray(res.data) ? res.data : res.data.data;
        setProducts(data || []);
      })
      .catch(err => {
        console.log("Product error", err);
        if (err.response?.status === 401) {
          localStorage.removeItem("adminToken");
          navigate("/admin/login");
        }
      });

  }, []);


  return (
    <>
      <AdminHeader />
      <div style={{ height: 80 }}></div>

      <div style={styles.wrapper}>
        <div style={styles.container}>

          {/* QUẢN LÝ SẢN PHẨM */}
          <div style={styles.card}>
            <div style={styles.cardHeader}>
              <h2 style={styles.cardTitle}>Quản Lý Sản Phẩm</h2>
              <Link to="/admin/products" style={styles.more}>Xem thêm</Link>
            </div>

            <div style={styles.row4}>

              <Link to="/admin/products" style={styles.itemBox}>
                <Package size={26} color="#ee4d2d" />
                <p>Số sản phẩm</p>
                <span>{products.length}</span>
              </Link>

              <Link to="/admin/products/add" style={styles.itemBox}>
                <Tag size={26} color="#3b82f6" />
                <p>Thêm sản phẩm</p>
                <span>Tạo mới</span>
              </Link>

              <Link to="/admin/discounts" style={styles.itemBox}>
                <ShoppingBag size={26} color="#a855f7" />
                <p>Mã giảm giá</p>
                <span>0</span>
              </Link>

            </div>
          </div>


          {/* ĐƠN HÀNG */}
          <div style={styles.card}>
            <div style={styles.cardHeader}>
              <h2 style={styles.cardTitle}>Quản Lý Đơn Hàng</h2>
              <Link to="/admin/orders" style={styles.more}>Xem thêm</Link>
            </div>

            <div style={styles.row4}>
              <Link to="/admin/orders" style={styles.itemBox}>
                <ShoppingBag size={26} color="#ef4444" />
                <p>Chờ xử lý</p>
                <span>0</span>
              </Link>
            </div>
          </div>

        </div>
      </div>
    </>
  );
}



/* =============== CSS STYLE =============== */

const styles = {
  wrapper: {
    padding: "20px 0",
    background: "#f5f5f5",
    minHeight: "100vh",
  },
  container: {
    maxWidth: 1380,
    margin: "0 auto",
    display: "flex",
    flexDirection: "column",
    gap: 20,
  },
  card: {
    background: "#fff",
    padding: "20px 25px",
    borderRadius: 12,
    border: "1px solid #eee",
  },
  cardHeader: {
    display: "flex",
    justifyContent: "space-between",
    marginBottom: 15,
  },
  cardTitle: {
    fontSize: 18,
    fontWeight: 600,
    color: "#333",
  },
  more: {
    color: "#007bff",
    fontSize: 14,
    textDecoration: "none",
  },
  row4: {
    display: "grid",
    gridTemplateColumns: "repeat(4, 1fr)",
    gap: 20,
  },
  itemBox: {
    border: "1px solid #eee",
    padding: "18px 20px",
    borderRadius: 10,
    background: "#fafafa",
    cursor: "pointer",
    textDecoration: "none",
    color: "#333",
    display: "flex",
    flexDirection: "column",
    gap: 8,
    fontSize: 14,
  },
};
