import React, { useState } from "react";
import axios from "axios";
import "./ReviewModal.css";

export default function ReviewModal({
  open,
  onClose,
  onReviewed,
  productId,
  productName,
}) {
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState("");
  const [loading, setLoading] = useState(false);

  if (!open) return null;
  if (!productId) return null;

  const handleSubmit = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("trip-token");

      await axios.post(
        `http://localhost:8080/api/reviews/${productId}`,
        { rating, comment },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      alert("Đánh giá thành công");

      setComment("");
      setRating(5);

      onReviewed(productId); // ⭐ đánh dấu reviewed luôn
      onClose();
    } catch (e) {
      const msg = e?.response?.data || "";

      // ⭐ nếu backend báo quá 24h hoặc đã đánh giá rồi
      if (
        msg.toLowerCase().includes("24") ||
        msg.toLowerCase().includes("đã đánh giá") ||
        msg.toLowerCase().includes("already")
      ) {
        onReviewed(productId);
        onClose();
        return;
      }

      console.log(e);
      alert(msg || "Không thể gửi đánh giá");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="review-modal-overlay">
      <div className="review-modal">
        <h3>Đánh giá sản phẩm</h3>
        <p className="product-name">{productName}</p>

        <div className="stars">
          {[1, 2, 3, 4, 5].map((s) => (
            <span
              key={s}
              className={s <= rating ? "active" : ""}
              onClick={() => setRating(s)}
            >
              ★
            </span>
          ))}
        </div>

        <textarea
          placeholder="Cảm nhận của bạn về sản phẩm..."
          value={comment}
          onChange={(e) => setComment(e.target.value)}
        />

        <div className="modal-actions">
          <button onClick={onClose} disabled={loading}>
            Đóng
          </button>
          <button onClick={handleSubmit} disabled={loading || !comment.trim()}>
            {loading ? "Đang gửi..." : "Gửi đánh giá"}
          </button>
        </div>
      </div>
    </div>
  );
}
