import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";

function TripDetail() {
  const { id } = useParams(); // tripId t·ª´ URL

  const steps = ["PLANNED", "ONGOING", "COMPLETED"];

  const categoryMap = {
    FOOD: "Food & Drinks",
    TRANSPORT: "Transport",
    TICKET: "Attractions & Tickets",
    SHOPPING: "Shopping & Souvenirs",
    OTHER: "Other",
  };

  // state
  const [currentStep, setCurrentStep] = useState(0);
  const [trip, setTrip] = useState(null);
  const [loading, setLoading] = useState(true);
  const [expenses, setExpenses] = useState({});
  const [maxDays, setMaxDays] = useState(0);

  // --- Load trip ---
  useEffect(() => {
    const fetchTrip = async () => {
      try {
        const res = await axios.get(`http://localhost:8080/api/trips/${id}`);
        const data = res.data;

        const stepIndex = steps.indexOf(data.status);
        setCurrentStep(stepIndex !== -1 ? stepIndex : 0);
        setTrip(data);

        if (data.startDate && data.endDate) {
          const start = new Date(data.startDate);
          const end = new Date(data.endDate);
          const days = Math.floor((end - start) / (1000 * 60 * 60 * 24)) + 1;
          setMaxDays(days);
        }
      } catch (err) {
        console.error("Error fetching trip:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchTrip();
  }, [id]);

  // --- Load expenses ---
  useEffect(() => {
    const fetchExpenses = async () => {
      try {
        const res = await axios.get(
          `http://localhost:8080/api/tripdays/trip/${id}`
        );
        const data = res.data; // list TripDayDTO t·ª´ backend
          console.log("DATA TRIP",data);
          
        // group theo day, d·∫°ng { day: { enumKey: {category, cost, note} } }
        const grouped = {};
        data.forEach((item) => {
          if (!grouped[item.day]) grouped[item.day] = {};
          grouped[item.day][item.itemExpense] = {
            category: categoryMap[item.itemExpense] || item.itemExpense,
            cost: item.price,
            note: item.note || "",
          };
        });
        // merge v·ªõi categoryMap -> lu√¥n ƒë·ªß 5 m·ª•c
        /*
         const normalized = {
         1:[

         ]
         };
        */
        const normalized = {};
        Object.keys(grouped).forEach((day) => {
          normalized[day] = Object.keys(categoryMap).map((key) => {            
            return (
              grouped[day][key] || {
                category: categoryMap[key],
                cost: "",
                note: "",
              }
            );
          });
        });

        setExpenses(normalized);
        setMaxDays(Object.keys(normalized).length);
      } catch (err) {
        console.error("Error fetching expenses:", err);
      }
    };

    fetchExpenses();
  }, [id]);
  // --- Handle input change ---
  const handleExpenseChange = (day, catIndex, field, value) => {
    setExpenses((prev) => {
      const updatedDay = [...(prev[day] || [])];
      updatedDay[catIndex] = { ...updatedDay[catIndex], [field]: value };
      return { ...prev, [day]: updatedDay };
    });
  };

  // --- Add new expense (POST) ---
  const handleAddExpense = async (day, catIndex) => {
    try {
      const expense = expenses[day][catIndex];
      const enumValue = Object.keys(categoryMap).find(
        (k) => categoryMap[k] === expense.category
      );
      console.log("expense", expense);

      await axios.post("http://localhost:8080/api/tripdays", {
        day: parseInt(day), // backend nh·∫≠n day
        itemExpense: enumValue,
        price: parseFloat(expense.cost),
        tripId: id,
        note: expense.note,
      });

      alert("ƒê√£ l∆∞u chi ti√™u!");
    } catch (err) {
      console.error("Add failed:", err);
      alert("Th√™m chi ti√™u th·∫•t b·∫°i!");
    }
  };

  // --- Add new empty day ---
  const handleAddDay = () => {
    const nextDay = Object.keys(expenses).length + 1;
    if (nextDay <= maxDays) {
      setExpenses((prev) => ({
        ...prev,
        [nextDay]: Object.values(categoryMap).map((c) => ({
          category: c,
          cost: "",
          note: "",
        })),
      }));
    }
  };

  // --- Cancel / Finish ---
  const handleCancel = async () => {
    try {
      await axios.put(`http://localhost:8080/api/trips/${id}/status`, {
        status: "CANCELED",
      });
      setTrip({ ...trip, status: "CANCELED" });
      alert("B·∫°n ƒë√£ hu·ª∑ chuy·∫øn ƒëi!");
    } catch (err) {
      console.error("Cancel failed:", err);
    }
  };

  const handleFinish = async () => {
    if (trip.status === "ONGOING") {
      try {
        await axios.put(`http://localhost:8080/api/trips/${id}/status`, {
          status: "COMPLETED",
        });
        setCurrentStep(2);
        setTrip({ ...trip, status: "COMPLETED" });
      } catch (err) {
        console.error("Finish failed:", err);
      }
    }
  };

  // --- Render ---
  if (loading) return <div className="container mt-4">ƒêang t·∫£i...</div>;
  if (!trip)
    return <div className="container mt-4">Kh√¥ng t√¨m th·∫•y chuy·∫øn ƒëi!</div>;

  return (
    <div className="container mt-4">
      {/* Trip Info */}
      <div className="card shadow-sm border-0 rounded-4 overflow-hidden mb-4">
        <img
          src={trip.image}
          alt={trip.name}
          className="card-img-top"
          style={{ height: "280px", objectFit: "cover" }}
        />
        <div className="card-body">
          <h2 className="fw-bold mb-5">{trip.name}</h2>
          <p>
            <strong>Tr·∫°ng th√°i:</strong> {trip.status}
          </p>
          <p>
            <strong>Ng√†y b·∫Øt ƒë·∫ßu:</strong>{" "}
            {new Date(trip.startDate).toLocaleDateString("vi-VN")}
          </p>
          <p>
            <strong>Ng√†y k·∫øt th√∫c:</strong>{" "}
            {new Date(trip.endDate).toLocaleDateString("vi-VN")}
          </p>
          <p>
            <strong>Chi ph√≠ ∆∞·ªõc t√≠nh:</strong>{" "}
            {trip.estimatedCost?.toLocaleString()} ‚Ç´
          </p>
          <p>
            <strong>Chi ph√≠ th·ª±c t·∫ø:</strong>{" "}
            {trip.actualCost?.toLocaleString()} ‚Ç´
          </p>
          <p>
            <strong>M√¥ t·∫£:</strong> {trip.description}
          </p>

          {/* Action */}
          <div className="mt-4 d-flex gap-2">
            {trip.status !== "CANCELED" && trip.status !== "COMPLETED" && (
              <button className="btn btn-outline-danger" onClick={handleCancel}>
                ‚ùå Cancel Trip
              </button>
            )}
            {trip.status === "ONGOING" && (
              <button className="btn btn-success" onClick={handleFinish}>
                ‚úÖ Finish Trip
              </button>
            )}
          </div>
        </div>
      </div>

      {/* Expenses Accordion */}
      <div className="mt-5">
        <h4 className="fw-bold mb-3">Chi ti√™u theo ng√†y</h4>
        <div className="accordion" id="expensesAccordion">
