import React, { useEffect, useState, useContext } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../css/page/ProductDetail.css";
import ProfileHeader from "../components/page/PageHeader";
import { DataContext } from "../DataContext";

export default function ProductDetail() {
  const { id } = useParams();
  const [p, setP] = useState(null);

  const { token } = useContext(DataContext);

  useEffect(() => {
    axios
      .get(`http://localhost:8080/api/products/${id}`, {
        headers: token ? { Authorization: `Bearer ${token}` } : {},
      })
      .then((res) => setP(res.data.data))
      .catch(() => setP(null));
  }, [id, token]);

  if (!p) return <div className="pd-loading">Loading…</div>;

  return (
    <div className="pd-container">

      <ProfileHeader />

      <div className="pd-wrapper">
        
        {/* IMG LEFT */}
        <div className="pd-left">
          <img src={p.images[0]?.imageUrl} className="pd-main-img" alt="" />

          <div className="pd-gallery">
            {p.images.map(img => (
              <img key={img.id} src={img.imageUrl} alt="" />
            ))}
          </div>
        </div>

        {/* INFO RIGHT */}
        <div className="pd-right">
          <h1 className="pd-title">{p.name}</h1>
          <div className="pd-price">{p.price.toLocaleString()} ₫</div>

          <ul className="pd-features">
            <li>Thương hiệu: {p.brand}</li>
            <li>Màu: {p.color}</li>
            <li>Tình trạng: {p.status}</li>
          </ul>

          <button className="pd-btn-cart">
            Thêm vào giỏ hàng
          </button>
        </div>
      </div>

      {/* DESCRIPTION */}
      <div className="pd-desc-section">
        <h2>Mô tả sản phẩm</h2>
        <p>{p.description}</p>
      </div>

      {/* SPECS */}
      <div className="pd-specs">
        <h2>Thông số kỹ thuật</h2>
        <table>
          <tbody>
            <tr><td>Màn hình</td><td>{p.screenSize}</td></tr>
            <tr><td>RAM</td><td>{p.ram}</td></tr>
            <tr><td>ROM</td><td>{p.storage}</td></tr>
            <tr><td>Chip</td><td>{p.chipset}</td></tr>
            <tr><td>Camera</td><td>{p.camera}</td></tr>
            <tr><td>Pin</td><td>{p.battery}</td></tr>
            <tr><td>Hệ điều hành</td><td>{p.os}</td></tr>
          </tbody>
        </table>
      </div>
    </div>
  );
}
