// src/pages/HomePage.jsx
import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import "../css/page/HomePage.css";
import ProfileHeader from "../components/page/PageHeader";
import { DataContext } from "../DataContext";
import { useNavigate } from "react-router-dom";

export default function HomePage() {

  const [products, setProducts] = useState([]);
  const [user, setUser] = useState(null);

  // l·∫•y token t·ª´ context
  const { token, setToken } = useContext(DataContext);  
  const navigate = useNavigate();

  /** GET USER n·∫øu c√≥ token */
  useEffect(() => {
    const localToken = localStorage.getItem("trip-token");

    if (!localToken) {
      setUser(null);
      return;
    }

    axios.get("http://localhost:8080/api/auth/user/me", {
      headers: { Authorization: `Bearer ${localToken}` },
    })
      .then(res => setUser(res.data.data))
      .catch(() => setUser(null));

  }, [token]);   // khi token thay ƒë·ªïi th√¨ reload user


  /** GET PRODUCTS */
  useEffect(() => {
    axios.get("http://localhost:8080/api/products")
      .then(res => {
        const list = res.data?.data || [];
        setProducts(list);
      })
      .catch(err => console.log(err));
  }, []);


  /** LOGOUT */
  const handleLogout = () => {
    localStorage.removeItem("trip-token");
    setToken(null);
    setUser(null);
    navigate("/");
  };


  return (
    <div className="hp-container">

      {/* Header */}
      <ProfileHeader
        user={user}
        onLogout={handleLogout}
      />

      {/* Banner */}
      <section className="hp-banner">
        <div className="hp-banner-slide">üî• Khuy·∫øn m√£i c·ª±c s·ªëc!</div>
      </section>

      {/* Categories */}
      <section className="hp-category">
        <div className="hp-cat-item">üì± ƒêi·ªán tho·∫°i</div>
        <div className="hp-cat-item">üíª Laptop</div>
        <div className="hp-cat-item">üéß √Çm thanh</div>
        <div className="hp-cat-item">üì∫ TV</div>
      </section>

      {/* FLASH SALE */}
      <section className="hp-section">
        <h2>üî• FLASH SALE</h2>
        <div className="hp-scroll-row">
          {products.map((p) => (
            <div
              key={p.id}
              className="hp-flash-card"
              onClick={() => navigate(`/product/${p.id}`)}
              style={{ cursor:"pointer" }}
            >
              <img src={p.images?.[0]?.imageUrl} alt={p.name} />
              <div className="hp-name">{p.name}</div>
              <div className="hp-price">{p.price.toLocaleString()} ‚Ç´</div>
            </div>
          ))}
        </div>
      </section>

      {/* Grid */}
      <section className="hp-section">
        <h2>G·ª£i √Ω h√¥m nay</h2>

        <div className="hp-grid">
          {products.map((p) => (
            <div
              key={p.id}
              className="hp-card"
              onClick={() => navigate(`/product/${p.id}`)}
              style={{ cursor:"pointer" }}
            >
              <img src={p.images?.[0]?.imageUrl} alt={p.name} />

              <div className="hp-info">
                <span className="hp-brand">{p.brand}</span>
                <h3>{p.name}</h3>
                <p>{p.price.toLocaleString()} ‚Ç´</p>
              </div>
            </div>
          ))}
        </div>
      </section>

    </div>
  );
}
